select add_profile_data ('AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE' , ' III - Administrative Classified ','Linux-based OS','https://www.stigviewer.com/stig/aix_6.1/2016-07-22/MAC-3_Classified/' , '2016-07-22' ,'Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-4371','SV-28400r1_rule','default','medium','The traceroute file must have mode 0700 or less permissive.','"If the mode of the traceroute executable is more permissive than 0700; malicious code could be inserted by an attacker and triggered whenever the traceroute command is executed by authorized users.  Additionally; if an unauthorized user is granted executable permissions to the traceroute command; it could be used to gain information about the network topology behind the firewall.  This information may allow an attacker to determine trusted routers and other network information possibly  leading to system and network compromise."','F-25671r1_fix','"Change the mode of the traceroute command. chmod 0700 /usr/bin/traceroute"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN004000','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-4370','SV-28397r1_rule','default','medium','"The traceroute command must be group-owned by sys; bin; or system."','"If the group owner of the traceroute command has not been set to a system group; unauthorized users could have access to the command and use it to gain information regarding a networks topology inside of the firewall.  This information may allow an attacker to determine trusted routers and other network information possibly leading to system and network compromise."','F-25668r1_fix','"Change the group owner of the traceroute command to sys; bin; or system.Procedure: chgrp system /usr/bin/traceroute"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003980','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22561','SV-38971r1_rule','default','medium','"If the system is using LDAP for authentication or account information; the /etc/ldap.conf (or equivalent) file must be group-owned by security; bin; sys; or system."','"LDAP can be used to provide user authentication and account information; which are vital to system security.  The LDAP client configuration must be protected from unauthorized modification."','F-33180r1_fix','"Change the group owner of the /etc/security/ldap/ldap.cfg file to security; bin; sys; or system.Procedure: chgrp security /etc/security/ldap/ldap.cfg"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN008100','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22560','SV-38970r1_rule','default','medium','"If the system is using LDAP for authentication or account information; the /etc/ldap.conf (or equivalent) file must be owned by root."','"LDAP can be used to provide user authentication and account information; which are vital to system security.  The LDAP client configuration must be protected from unauthorized modification."','F-33179r1_fix','"Change the owner of the /etc/security/ldap/ldap.cfg file. chown root /etc/security/ldap/ldap.cfg"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN008080','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-4367','SV-27393r1_rule','default','medium','"The at.allow file must be owned by root; bin; or sys."','"If the owner of the at.allow file is not set to root; bin; or sys; unauthorized users could be allowed to view or edit sensitive information contained within the file."','F-24641r1_fix','"Change the owner of the at.allow file. chown root /var/adm/cron/at.allow"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003460','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-4701','SV-27440r1_rule','default','low','The system must not have the finger service active.','"The finger service provides information about the systems users to network clients.  This information could expose information that could be used in subsequent attacks."','F-24712r1_fix','Edit /etc/inetd.conf and comment out the finger service line.  Restart the inetd service.','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003860','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-831','SV-40836r1_rule','default','medium','The alias file must be owned by root.','"If the alias file is not owned by root; an unauthorized user may modify the file to add aliases to run malicious code or redirect email."','F-985r2_fix','"Change the owner of the /etc/mail/aliases file (or equivalent; such as /usr/lib/aliases) to root.Procedure: chown root /etc/mail/aliases"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN004360','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22488','SV-40723r1_rule','default','medium','The SSH daemon must not allow compression or must only allow compression after successful authentication.','"If compression is allowed in an SSH connection prior to authentication; vulnerabilities in the compression software could result in compromise of the system from an unauthenticated connection; potentially with root privileges."','F-34582r1_fix','"Edit the /etc/ssh/sshd_config file and remove the Compression setting or set the Compression setting to ""delayed"" or ""no""."','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN005539','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-24386','SV-38953r1_rule','default','high','The telnet daemon must not be running.','"The telnet daemon provides a typically unencrypted remote access service which does not provide for the confidentiality and integrity of user passwords or the remote session.  If a privileged user were to log on using this service; the privileged user password could be compromised."','F-31868r1_fix','"Edit the /etc/inetd.conf file and comment out the telnet line.Reload the inetd process.   refresh -s inetd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003850','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22486','SV-40721r1_rule','default','medium','The SSH daemon must use privilege separation.','"SSH daemon privilege separation causes the SSH process to drop root privileges when not needed; which would decrease the impact of software vulnerabilities in the unprivileged section."','F-34580r1_fix','"Edit the /etc/ssh/sshd_config file and remove the UsePrivilegeSeparation setting or change the value of the UsePrivilegeSeparation setting to ""yes""."','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN005537','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22487','SV-40722r1_rule','default','medium','The SSH daemon must not allow rhosts RSA authentication.','"If SSH permits rhosts RSA authentication; a user may be able to log in based on the keys of the host originating the request and not any user-specific authentication."','F-34581r1_fix','"Edit the /etc/ssh/sshd_config file and remove the RhostsRSAAuthentication setting or change the value of the RhostsRSAAuthentication setting to ""no""."','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN005538','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-11975','SV-38675r1_rule','default','medium','The system must require passwords to contain no more than three consecutive repeating characters.','"To enforce the use of complex passwords; the number of consecutive repeating characters is limited.  Passwords with excessive repeated characters may be more vulnerable to password-guessing attacks."','F-32056r1_fix','"Use the chsec command to set maxrepeats to 3. chsec -f /etc/security/user -s default -a maxrepeats=3 chuser maxrepeats=3 < user id >"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000680','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22485','SV-40720r1_rule','default','medium','The SSH daemon must perform strict mode checking of home directory configuration files.','"If other users have access to modify user-specific SSH configuration files; they may be able to log into the system as another user."','F-34579r1_fix','"Edit the /etc/sshd/sshd_config file and remove the StrictModes setting or change the value of the StrictModes setting to ""yes""."','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN005536','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-768','SV-38839r1_rule','default','medium','The delay between login prompts following a failed login attempt must be at least 4 seconds.','Enforcing a delay between successive failed login attempts increases protection against automated password guessing attacks.','F-33091r1_fix','"Use vi  or the chsec command to change the login delay time period.chsec -f /etc/security/login.cfg -s default -a logindelay=4   OR vi /etc/security/login.cfg Add logindelay = 4 to the default stanza."','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000480','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1032','SV-38768r1_rule','default','medium','Users must not be able to change passwords more than once every 24 hours.','The ability to change passwords frequently facilitates users reusing the same password. This can result in users effectively never changing their passwords. This would be accomplished by users changing their passwords when required and then immediately changing it to the original value.','F-33201r1_fix','"Use SMIT or the chsec command to set the minimum password age to 1 week. chsec -f /etc/security/user -s default -a minage=1  chsec -f /etc/security/user -s <user id> -a minage=1OR smitty chuser"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000540','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-766','SV-38671r1_rule','default','medium','The system must disable accounts after three consecutive unsuccessful login attempts.','Disabling accounts after a limited number of unsuccessful login attempts improves protection against password guessing attacks.','F-31633r1_fix','"Use the chsec command to configure the number of unsuccessful logins resulting in account lockout.   chsec -f /etc/security/user -s default -a loginretries=3  chsec -f /etc/security/user -s <user id> -a loginretries=3"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000460','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-763','SV-38932r1_rule','default','medium','"The Department of Defense (DoD) login banner must be displayed immediately prior to; or as part of; console login prompts."','Failure to display the login banner prior to a logon attempt will negate legal proceedings resulting from unauthorized access to system resources.','F-31626r1_fix','"Edit /etc/security/login.cfg and assign the herald value for the default and /dev/console stanzas to one of the DoD login banners (based on the character limitations imposed by the system). chsec -f /etc/security/login.cfg -s default -a herald=""<DoD Login Banner>""OR vi /etc/security/login.cfg and add a herald = <DoD Login Banner> statement to the default stanzaDoD Login Banners:""You are accessing a U.S. Government (USG) Information System (IS) that is provided for USG-authorized use only.By using this IS (which includes any device attached to this IS); you consent to the following conditions:-The USG routinely intercepts and monitors communications on this IS for purposes including; but not limited to; penetration testing; COMSEC monitoring; network operations and defense; personnel misconduct (PM); law enforcement (LE); and counterintelligence (CI) investigations.-At any time; the USG may inspect and seize data stored on this IS.-Communications using; or data stored on; this IS are not private; are subject to routine monitoring; interception; and search; and may be disclosed or used for any USG-authorized purpose.-This IS includes security measures (e.g.; authentication and access controls) to protect USG interests--not for your personal benefit or privacy.-Notwithstanding the above; using this IS does not constitute consent to PM; LE or CI investigative searching or monitoring of the content of privileged communications; or work product; related to personal representation or services by attorneys; psychotherapists; or clergy; and their assistants. Such communications and work product are private and confidential. See User Agreement for details. ""OR""Ive read AND consent to terms in IS user agreemt."""','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000400','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22473','SV-40714r1_rule','default','low','The SSH daemon must not permit GSSAPI authentication unless needed.','"GSSAPI authentication is used to provide additional authentication mechanisms to applications. Allowing GSSAPI authentication through SSH exposes the system’s GSSAPI to remote hosts; increasing the attack surface of the system.  GSSAPI authentication must be disabled unless needed."','F-34572r1_fix','"Edit /etc/ssh/sshd_config and remove the GSSAPIAuthentication setting or change the value to ""no""."','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN005524','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22472','SV-26765r1_rule','default','medium','The SSH private host key files must have mode 0600 or less permissive.','"If an unauthorized user obtains the private SSH host key file; the host could be impersonated."','F-24015r1_fix','"Change the permissions for the SSH private host key files. chmod 0600 /etc/ssh/*key"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN005523','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22471','SV-26764r1_rule','default','medium','The SSH public host key files must have mode 0644 or less permissive.','"If a public host key file is modified by an unauthorized user; the SSH service may be compromised."','F-24014r1_fix','"Change the permissions for the SSH public host key files. chmod 0644 /etc/ssh/*key.pub"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN005522','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22470','SV-26763r1_rule','default','medium','The SSH daemon must restrict login ability to specific users and/or groups.','"Restricting SSH logins to a limited group of users; such as system administrators; prevents password-guessing and other SSH attacks from reaching system accounts and other accounts not authorized for SSH access."','F-24013r1_fix','Edit the SSH daemon configuration and add an AllowGroups directive.','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN005521','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29491','SV-38695r1_rule','default','medium','The /etc/netsvc.conf file must be root owned.','"The /etc/netsvc.conf file is used to specify the ordering of name resolution for the sendmail command;  alias resolution for the sendmail command; and host name resolution routines.    Malicious changes could prevent the system from functioning correctly or compromise system security."','F-33710r1_fix','"Change the owner of the /etc/netsvc.conf file to root.  chown root /etc/netsvc.conf"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000000-AIX0085','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29492','SV-38696r1_rule','default','medium','"The /etc/netsvc.conf file must be group-owned by bin; sys; or system."','"The /etc/netsvc.conf file is used to specify the ordering of name resolution for the sendmail command;  alias resolution for the sendmail command; and host name resolution routines.    Malicious changes could prevent the system from functioning correctly or compromise system security."','F-33050r1_fix','"Change the group owner of the /etc/netsvc.conf file to bin; sys; or system.Procedure: chgrp system /etc/netsvc.conf"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000000-AIX0090','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22474','SV-40715r1_rule','default','low','The SSH client must not permit GSSAPI authentication unless needed.','"GSSAPI authentication is used to provide additional authentication mechanisms to applications. Allowing GSSAPI authentication through SSH exposes the system’s GSSAPI to remote hosts; increasing the attack surface of the system.  GSSAPI authentication must be disabled unless needed."','F-34574r1_fix','"Edit the /etc/ssh/ssh_config file and remove the GSSAPIAuthentication setting or change the GSSAPIAuthentication setting to ""no""."','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN005525','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-12030','SV-41532r1_rule','default','medium','The systems access control program must be configured to grant or deny system access to specific hosts.','"If the systems access control program is not configured with appropriate rules for allowing and denying access to system network resources; services may be accessible to unauthorized hosts."','F-11287r2_fix','Edit the /etc/hosts.allow and /etc/hosts.deny files to configure access restrictions.','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN006620','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29498','SV-38702r1_rule','default','medium','The system must provide protection against IP fragmentation attacks.','The parameter ip_nfrag provides an additional layer of protection against IP fragmentation attacks.  The value the ip_nfrag specifies is the maximum number of fragments of an IP packet that can be kept in the IP reassembly queue at any time. The default value of this network option is 200.  This is a reasonable value for most environments and offers protection from IP fragmentation attacks.','F-33056r1_fix','"Set the ip_nfrag parameter to 200. /usr/sbin/no -p -o ip_nfrag=200"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000000-AIX0230','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-12035','SV-12536r2_rule','default','high','The SYSTEM attribute must not be set to NONE for any account.','"The SYSTEM attribute in /etc/security/user defines the mechanisms used to authenticate specific user accounts.  If the value is set to NONE; other attributes will be used to determine the authentication mechanisms; but if these attributes are not present; no authentication will be performed.  To ensure authentication is always used for the systems accounts; the SYSTEM attribute must always be set to a valid setting other than NONE."','F-11292r2_fix','Edit /etc/security/user and change any SYSTEM=NONE settings to a valid authentication setting.','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000000-AIX00080','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22332','SV-26425r1_rule','default','medium','The /etc/passwd file must be owned by root.','The /etc/passwd file contains the list of local system accounts.  It is vital to system security and must be protected from unauthorized modification.','F-23612r1_fix','"Change the owner of the /etc/passwd file to root. chown root /etc/passwd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001378','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-4387','SV-4387r2_rule','default','high','Anonymous FTP accounts must not have a functional shell.','"If an anonymous FTP account has been configured to use a functional shell; attackers could gain access to the shell if the account is compromised."','F-4298r2_fix','"Configure anonymous FTP accounts to use a non-functional shell. If necessary; edit the /etc/passwd file to remove any functioning shells associated with the FTP account and replace them with non-functioning shells; such as; /dev/null."','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN005000','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-4385','SV-4385r2_rule','default','medium','The system must not use .forward files.','The .forward file allows users to automatically forward mail to another system. Use of .forward files could allow the unauthorized forwarding of mail and could potentially create mail loops which could degrade system performance.','F-4296r2_fix','Remove .forward files from the system.','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN004580','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29515','SV-38719r1_rule','default','medium','The system must not have the rusersd service active.','The rusersd daemon gives out a list of current uses on the system.   The rusersd daemon is unnecessary and it increases the attack vector of the system by providing information on the current users of the system.','F-33073r1_fix','"Edit the /etc/inetd.conf file and comment out the rusersd service line. Restart the inetd service.    refresh -s inetd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN009310','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-788','SV-38735r1_rule','default','medium','All skeleton files (typically those in /etc/skel) must have mode 0644 or less permissive.','"If the skeleton files are not protected; unauthorized personnel could change user startup parameters and possibly jeopardize user files."','F-32450r1_fix','"Change the mode of skeleton files with incorrect mode. chmod 0644 /etc/security/.profile  chmod 0755 /etc/security/mkuser.sys"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001800','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-11947','SV-38936r2_rule','default','medium','The system must require passwords to contain a minimum of 15 characters.','The use of longer passwords reduces the ability of attackers to successfully obtain valid passwords using guessing or exhaustive search techniques by increasing the password search space.','F-31635r2_fix','"Change the minimum password length to 15 or more.  chsec -f /etc/security/user -s default -a minlen=15 chuser minlen=15 <user id>"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000580','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-11940','SV-27052r1_rule','default','high','The operating system must be a supported release.','"An operating system release is considered ""supported"" if the vendor continues to provide security patches for the product.  With an unsupported release; it will not be possible to resolve security issues discovered in the system software."','F-11211r2_fix','Upgrade to a supported version of the operating system.','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000100','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-778','SV-38683r1_rule','default','medium','The system must prevent the root account from directly logging in except from the system console.','Limiting the root account direct logins to only system consoles protects the root account from direct unauthorized access from a non-console device.','F-32196r1_fix','"The root account can be protected from non-console device logins by setting rlogin = false in the root: stanza of the /etc/security/user file. chsec -f /etc/security/user -s root -a rlogin=false"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000980','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-800','SV-38728r1_rule','default','medium','The /etc/security/passwd file must have mode 0400.','The /etc/security/passwd file contains the list of local system accounts.  It is vital to system security and must be protected from unauthorized modification.  The file also contains password hashes which must not be accessible to users other than root.','F-32304r2_fix','"Change the mode of the /etc/security/passwd file. chmod 0400 /etc/security/passwd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001420','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-777','SV-777r2_rule','default','medium','The root account must not have world-writable directories in its executable search path.','"If the root search path contains a world-writable directory; malicious software could be placed in the path by intruders and/or malicious users and inadvertently run by root with all of roots privileges."','F-24415r1_fix','"For each world-writable path in roots executable search path; perform one of the following.1. Remove the world-writable permission on the directory.Procedure: chmod o-w <path>2. Remove the world-writable directory from the executable search path.Procedure:Identify and edit the initialization file referencing the world-writable directory and remove it from the PATH variable."','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000960','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-774','SV-38940r1_rule','default','low','The root users home directory must not be the root directory (/).','Changing the root home directory to something other than / and assigning it a 0700 protection makes it more difficult for intruders to manipulate the system by reading the files that root places in its default directory. It also gives root the same discretionary access control for roots home directory as for the other plain user home directories.','F-32113r1_fix','"The root home directory should be something other than / (such as /root).Procedure: mkdir /root chown root /root chgrp sys /root chmod 700 /root cp -r /.??* /root/.Then; edit the passwd file and change the root home directory to /root. The cp -r /.??* command copies all files and subdirectories of file names that begin with ""."" into the new root directory; which preserves the previous root environment. Must be in the ""/"" directory when executing the ""cp"" command."','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000900','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-775','SV-38941r1_rule','default','medium','The root accounts home directory (other than /) must have mode 0700.','Permissions greater than 0700 could allow unauthorized users access to the root home directory.','F-32119r1_fix','"The root home directory will have permissions of 0700. Do not change the protections of the / directory. Use the following command to change protections for the root home directory. chmod 0700 /root."','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000920','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-773','SV-773r2_rule','default','medium','The root account must be the only account having an UID of 0.','"If an account has an UID of 0; it has root authority. Multiple accounts with an UID of 0 afford more opportunity for potential intruders to guess a password for a privileged account."','F-24403r1_fix','Remove or change the UID of accounts other than root that have UID 0.','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000880','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-770','SV-27107r1_rule','default','high','The system must not have accounts configured with blank or null passwords.','"If an account is configured for password authentication but does not have an assigned password; it may be possible to log into the account without authentication.  If the root user is configured without a password; the entire system may be compromised.  For user accounts not using password authentication; the account must be configured with a password lock value instead of a blank or null value.  "','F-33344r1_fix','"Remove or configure a password for any account with a blank password.   passwd <user id> smitty passwdTo remove an account with a blank password. smitty rmuser"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000560','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22462','SV-26755r1_rule','default','medium','The SSH client must be configured to not use CBC-based ciphers.','"The Cipher-Block Chaining (CBC) mode of encryption as implemented in the SSHv2 protocol is vulnerable to chosen plain text attacks and must not be used."','F-24004r1_fix','"Edit /etc/ssh/ssh_config and add or edit the ""Ciphers"" line.  Only include ciphers that start with ""3des"" or ""aes"" and do not contain ""cbc"".  For the list of available ciphers for the particular version of your software; consult the ssh_config manpage."','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN005511','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22463','SV-26756r2_rule','default','medium','The SSH client must be configured to only use Message Authentication Codes (MACs) employing FIPS 140-2 approved cryptographic hash algorithms.','DoD information systems are required to use FIPS 140-2 approved cryptographic hash functions.','F-24006r2_fix','"Edit the SSH client configuration and remove any MACs that are not hmac-sha1 or a better hmac algorithm that is on the FIPS 140-2 approved list. If necessary; add a MACs line."','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN005512','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-4084','SV-38679r1_rule','default','medium','The system must prohibit the reuse of passwords within five iterations.','"If a user; or root; used the same password continuously or was allowed to change it back shortly after being forced to change it to something else; it would provide a potential intruder with the opportunity to keep guessing at one users password until it was guessed correctly."','F-32090r1_fix','"Use the chsec command to configure the system to prohibit the reuse of passwords within five iterations. chsec -f /etc/security/user -s default -a histsize=5 chuser histsize=5 < user id >"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000800','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22553','SV-38827r1_rule','default','medium','The system must not forward IPv6 source-routed packets.','"Source-routed packets allow the source of the packet to suggest routers forward the packet along a different path than configured on the router; which can be used to bypass network security measures. This requirement applies only to the forwarding of source-routed traffic; such as when IPv6 forwarding is enabled and the system is functioning as a router. "','F-32350r1_fix','"Configure the system so it does not forward IPv6 source-routed packets.   /usr/sbin/no -p -o ip6srcrouteforward=0"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN007920','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-981','SV-39104r1_rule','default','medium','"Cron and crontab directories must be group-owned by system; sys; bin; or cron."','"To protect the integrity of scheduled system jobs and to prevent malicious modification to these jobs; crontab files must be secured.  Failure to give group ownership of cron or crontab directories to a system group provides the designated group and unauthorized users with the potential to access sensitive information or change the system configuration which could weaken the systems security posture."','F-33357r1_fix','"Change the group owner of the crontab directories to sys; system; bin; or cron. Procedure:  chown cron /var/spool/cron/crontabs"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003140','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-980','SV-27345r1_rule','default','medium','Cron and crontab directories must be owned by root or bin.','Incorrect ownership of the cron or crontab directories could permit unauthorized users the ability to alter cron jobs and run automated jobs as privileged users.  Failure to give ownership of cron or crontab directories to root or to bin provides the designated owner and unauthorized users with the potential to access sensitive information or change the system configuration which could weaken the systems security posture.','F-24583r1_fix','"Change the owner of the crontab directory. chown root /var/spool/cron/crontabs"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003120','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22290','SV-39091r1_rule','default','medium','"The system clock must be synchronized continuously; or at least daily."','"A synchronized system clock is critical for the enforcement of time-based policies and the correlation of logs and audit records with other systems.  Internal system clocks tend to drift and require periodic resynchronization to ensure their accuracy.  Software; such as ntpd; can be used to continuously synchronize the system clock with authoritative sources.  Alternatively; the system may be synchronized periodically; with a maximum of one day between synchronizations.If the system is completely isolated (no connections to networks or other systems); time synchronization is not required as no correlation of events or operation of time-dependent protocols between systems will be necessary. If the system is completely isolated; this requirement is not applicable."','F-33324r1_fix','"Enable the NTP daemon for continuous synchronization. Edit /etc/rc.tcpip and enable xntpd daemon. Edit /etc/ntp.conf and add the ntp server entry.  startsrc -s xntpd OR Add a daily or more frequent cronjob to perform synchronization using ntpdate. NOTE: While it is possible to run ntpdate from a cron script; it is important to mention that ntpdate with contrived cron scripts is no substitute for the NTP daemon; which uses sophisticated algorithms to maximize accuracy and reliability while minimizing resource use.Finally; since ntpdate polling does not discipline the host clock frequency as does xntpd; the accuracy using ntpdate is limited. The process of passively listening for NTP broadcasts (i.e.; placing the line broadcastclient yes in the /etc/ntp.conf file) is preferred over any procedural form of direct server polling for a large network with many nodes needing to be time synchronized. This method is preferred because it significantly reduces the network traffic load related to NTP."','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000241','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-985','SV-27381r1_rule','default','medium','The at.deny file must not be empty if it exists.','"On some systems; if there is no at.allow file and there is an empty at.deny file; then the system assumes everyone has permission to use the at facility.  This could create an insecure setting in the case of malicious users or system intruders."','F-1139r2_fix','"Add appropriate users to the at.deny file; or remove the empty at.deny file if an at.allow file exists."','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003300','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-984','SV-27377r1_rule','default','medium','Access to the at utility must be controlled via the at.allow and/or at.deny file(s).','"The at facility selectively allows users to execute jobs at deferred times.  It is usually used for one-time jobs. The at.allow file selectively allows access to the at facility.  If there is no at.allow file; there is no ready documentation of who is allowed to submit at jobs."','F-11346r2_fix','"Create at.allow and/or at.deny files containing appropriate lists of users to be allowed or denied access to the ""at"" daemon."','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003280','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-987','SV-27389r1_rule','default','medium','The at.allow file must have mode 0600 or less permissive.','"Permissions more permissive than 0600 (read; write and execute for the owner) may allow unauthorized or malicious access to the at.allow and/or at.deny files."','F-24633r1_fix','"Change the mode of the at.allow file. chmod 0600 /var/adm/cron/at.allow"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003340','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-23741','SV-38796r1_rule','default','medium','TCP backlog queue sizes must be set appropriately.','"To provide some mitigation to TCP DoS attacks; the clear_partial_conns parameter must be enabled."','F-32491r1_fix','/usr/sbin/no -po clean_partial_conns=1','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003601','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-4394','SV-40364r1_rule','default','medium','"The /etc/syslog.conf file must be group-owned by bin; sys; or system."','"If the group owner of /etc/syslog.conf is not root; bin; or sys; unauthorized users could be permitted to view; edit; or delete important system messages handled by the syslog facility."','F-34348r1_fix','"Change the group owner of the /etc/syslog.conf file to bin; sys; or system.Procedure: chgrp system /etc/syslog.conf"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN005420','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22294','SV-40383r1_rule','default','medium','The time synchronization configuration file (such as /etc/ntp.conf) must be owned by root.','"A synchronized system clock is critical for the enforcement of time-based policies and the correlation of logs and audit records with other systems.  If an illicit time source is used for synchronization; the integrity of system logs and the security of the system could be compromised.  If the configuration files controlling time synchronization are not owned by a system account; unauthorized modifications could result in the failure of time synchronization."','F-34350r1_fix','"Change the owner of the ntp.conf file. chown root ntp.conf"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000250','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-4393','SV-4393r2_rule','default','medium','The /etc/syslog.conf file must be owned by root.','"If the /etc/syslog.conf file is not owned by root; unauthorized users could be allowed to view; edit; or delete important system messages handled by the syslog facility."','F-4304r2_fix','"Use the chown command to set the owner to root. chown root /etc/syslog.conf"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN005400','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-789','SV-38775r1_rule','default','medium','"NIS/NIS+/yp files must be owned by root; sys; or bin."','"NIS/NIS+/yp files are part of the systems identification and authentication processes and are; therefore; critical to system security.  Failure to give ownership of sensitive files or utilities to root or bin provides the designated owner and unauthorized users with the potential to access sensitive information or change the system configuration which could weaken the systems security posture."','F-35060r1_fix','"Change the owner of the NIS files to root; sys; or bin.Procedure (example): chown root < directory>/< file >"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001320','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-974','SV-27318r1_rule','default','medium','Access to the cron utility must be controlled using the cron.allow and/or cron.deny file(s).','"The cron facility allows users to execute recurring jobs on a regular and unattended basis.  The cron.allow file designates accounts allowed to enter and execute jobs using the cron facility.  If neither cron.allow nor cron.deny exists; then any account may use the cron facility.  This may open the facility up for abuse by system intruders and malicious users."','F-24558r1_fix','Create /var/adm/cron/cron.allow and/or /var/adm/cron/cron.deny with appropriate content.','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN002960','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-975','SV-27324r1_rule','default','medium','The cron.allow file must have mode 0600 or less permissive.','"A cron.allow file that is readable and/or writable by other than root could allow potential intruders and malicious users to use the file contents to help discern information; such as who is allowed to execute cron programs; which could be harmful to overall system and network security."','F-24564r1_fix','"Change the mode of the cron.allow file to 0600.Procedure: chmod 0600 /var/adm/cron/cron.allow"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN002980','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22391','SV-39346r1_rule','default','medium','"The cron.allow file must be group-owned by system; bin; sys; or cron."','" If the group of the cron.allow is not set to system; bin; sys; or cron; the possibility exists for an unauthorized user to view or edit the list of users permitted to use cron. Unauthorized modification of this file could cause Denial of Service to authorized cron users or provide unauthorized users with the ability to run cron jobs."','F-33580r1_fix','"Change the group owner of the cron.allow file to bin; sys; system; or cron. Procedure:  chgrp cron /var/adm/cron/cron.allow"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003250','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22348','SV-26447r1_rule','default','medium','The /etc/group file must not contain any group password hashes.','"Group passwords are typically shared and should not be used.  Additionally; if password hashes are readable by non-administrators; the passwords are subject to attack through lookup tables or cryptographic weaknesses in the hashes."','F-23639r1_fix','Edit /etc/group and change the password field to an exclamation point (!) to lock the group password.','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001475','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-978','SV-27340r1_rule','default','medium','Crontab files must have mode 0600 or less permissive.','"To protect the integrity of scheduled system jobs and prevent malicious modification to these jobs; crontab files must be secured."','F-24579r1_fix','"Change the mode of the crontab files. chmod 0600 /var/spool/cron/crontabs/*"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003080','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-979','SV-27342r1_rule','default','medium','Cron and crontab directories must have mode 0755 or less permissive.','"To protect the integrity of scheduled system jobs and to prevent malicious modification to these jobs; crontab files must be secured."','F-24581r1_fix','"Change the mode of the crontab directory. chmod 0755 /var/spool/cron/crontabs"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003100','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22324','SV-39100r1_rule','default','medium','"The /etc/hosts file must be group-owned by bin; sys; or system."','"The /etc/hosts file (or equivalent) configures local host name to IP address mappings that typically take precedence over DNS resolution.  If this file is maliciously modified; it could cause the failure or compromise of security functions requiring name resolution; which may include time synchronization; centralized authentication; and remote system logging."','F-33351r1_fix','"Change the group owner of the /etc/hosts file to sys; bin; or system.Procedure: chgrp system /etc/hosts"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001367','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1028','SV-39229r1_rule','default','medium','The /usr/lib/smb.conf file must have mode 0644 or less permissive.','"If the smb.conf file has excessive permissions; the file may be maliciously modified and the Samba configuration could be compromised."','F-34584r1_fix','"Change the mode of the smb.conf file to 0644 or less permissive.Procedure: chmod 0644 /usr/lib/smb.conf"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN006140','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1029','SV-40379r1_rule','default','medium','The /var/private/smbpasswd file must be owned by root.','"If the smbpasswd file is not owned by root; the smbpasswd file may be maliciously accessed or modified; potentially resulting in the compromise of Samba accounts."','F-34347r1_fix','"Change the owner of the smbpasswd file to root. chown root /var/private/smbpasswd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN006160','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22325','SV-26412r1_rule','default','medium','The /etc/hosts file must have mode 0644 or less permissive.','"The /etc/hosts file (or equivalent) configures local host name to IP address mappings that typically take precedence over DNS resolution.  If this file is maliciously modified; it could cause the failure or compromise of security functions requiring name resolution; which may include time synchronization; centralized authentication; and remote system logging."','F-23599r1_fix','"Change the mode of the /etc/hosts file to 0644 or less permissive. chmod 0644 /etc/hosts"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001368','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1027','SV-40724r1_rule','default','medium','The /usr/lib/smb.conf file must be owned by root.','"The /usr/lib/smb.conf file allows access to other machines on the network and grants permissions to certain users.  If it is owned by another user; the file may be maliciously modified and the Samba configuration could be compromised."','F-34583r1_fix','"Change the ownership of the smb.conf file.Procedure: chown root /usr/lib/smb.conf"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN006100','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22323','SV-26410r2_rule','default','medium','The /etc/hosts file must be owned by root.','"The /etc/hosts file (or equivalent) configures local host name to IP address mappings that typically take precedence over DNS resolution.  If this file is maliciously modified; it could cause the failure or compromise of security functions requiring name resolution; which may include time synchronization; centralized authentication; and remote system logging."','F-23597r1_fix','"Change the owner of the /etc/hosts file to root. chown root /etc/hosts"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001366','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22453','SV-26740r1_rule','default','medium','The /etc/syslog.conf file must have mode 0640 or less permissive.','Unauthorized users must not be allowed to access or modify the /etc/syslog.conf file.','F-23989r1_fix','"Change the permissions of the syslog configuration file. chmod 0640 /etc/syslog.conf"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN005390','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22320','SV-39099r1_rule','default','medium','"The /etc/resolv.conf file must be group-owned by bin; sys; or system."','"The resolv.conf (or equivalent) file configures the systems DNS resolver.  DNS is used to resolve host names to IP addresses.  If DNS configuration is modified maliciously; host name resolution may fail or return incorrect information.  DNS may be used by a variety of system security functions; such as time synchronization; centralized authentication; and remote system logging."','F-33350r1_fix','"Change the group owner of the /etc/resolv.conf file to bin; sys; or system.Procedure: chgrp system /etc/resolv.conf"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001363','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-776','SV-40085r2_rule','default','medium','The root accounts executable search path must be the vendor default and must contain only absolute paths.','"The executable search path (typically the PATH environment variable) contains a list of directories for the shell to search to find executables. If this path includes the current working directory or other relative paths; executables in these directories may be executed instead of system commands. This variable is formatted as a colon-separated list of directories. If there is an empty entry; such as a leading or trailing colon or two consecutive colons; this is interpreted as the current working directory. Entries starting with a slash (/) are absolute paths."','F-34156r1_fix','"Edit the root users local initialization files. Change any found PATH variable settings to the vendors default path for the root user. Remove any empty path entries or references to relative paths. cd <roots home directory> vi .profile .cshrcIf the bash shell is installed; edit these additional files. vi .bashrc .bash_profile"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000940','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22321','SV-26397r1_rule','default','medium','The /etc/resolv.conf file must have mode 0644 or less permissive.','"The resolv.conf (or equivalent) file configures the systems DNS resolver.  DNS is used to resolve host names to IP addresses.  If DNS configuration is modified maliciously; host name resolution may fail or return incorrect information.  DNS may be used by a variety of system security functions; such as time synchronization; centralized authentication; and remote system logging."','F-23588r1_fix','"Change the mode of the /etc/resolv.conf file to 0644 or less permissive. chmod 0644 /etc/resolv.conf"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001364','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-23732','SV-38934r1_rule','default','medium','The FTPS/FTP service on the system must be configured with the Department of Defense (DoD) login banner.','"Failure to display the login banner prior to a logon attempt will negate legal proceedings resulting from unauthorized access to system resources.NOTE: SFTP and FTPS are encrypted alternatives to FTP and should be used in place of FTP. SFTP is implemented by the SSH service and uses its banner configuration."','F-31629r1_fix','"Configure the system to display one of the DoD login banners (based on the character limitations imposed by the system) prior to any FTP login attempt.Add a banner file to the system with the DoD login banner.vi /etc/herald<Add DoD banner to file>chmod 644 /etc/heraldchown root:system /etc/heraldAdd a herald line to the /etc/ftpaccess.ctl file.vi /etc/ftpaccess.ctl<add/update line in /etc/ftpaccess.ctl> herald: /etc/heraldchown root:system /etc/ftpaccess.ctlchmod 640 /etc/ftpaccess.ctlDoD Login Banners:""You are accessing a U.S. Government (USG) Information System (IS) that is provided for USG-authorized use only.By using this IS (which includes any device attached to this IS); you consent to the following conditions:-The USG routinely intercepts and monitors communications on this IS for purposes including; but not limited to; penetration testing; COMSEC monitoring; network operations and defense; personnel misconduct (PM); law enforcement (LE); and counterintelligence (CI) investigations.-At any time; the USG may inspect and seize data stored on this IS.-Communications using; or data stored on; this IS are not private; are subject to routine monitoring; interception; and search; and may be disclosed or used for any USG-authorized purpose.-This IS includes security measures (e.g.; authentication and access controls) to protect USG interests--not for your personal benefit or privacy.-Notwithstanding the above; using this IS does not constitute consent to PM; LE or CI investigative searching or monitoring of the content of privileged communications; or work product; related to personal representation or services by attorneys; psychotherapists; or clergy; and their assistants. Such communications and work product are private and confidential. See User Agreement for details.""OR""Ive read AND consent to terms in IS user agreemt."""','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000410','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-11984','SV-38737r1_rule','default','medium','All skeleton files and directories (typically in /etc/skel) must be owned by root or bin.','"If the skeleton files are not protected; unauthorized personnel could change user startup parameters and possibly jeopardize user files.  Failure to give ownership of sensitive files or utilities to root or bin provides the designated owner and unauthorized users with the potential to access sensitive information or change the system configuration which could weaken the systems security posture."','F-32452r1_fix','"Change the ownership of skeleton files with incorrect mode. chown root /etc/security/.profile /etc/security/mkuser.sys"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001820','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-901','SV-901r2_rule','default','medium','All users home directories must have mode 0750 or less permissive.','Excessive permissions on home directories allow unauthorized access to users files.','F-1055r2_fix','"Change the mode of users home directories to 0750 or less permissive.Procedure (example): chmod 0750 <home directory>"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001480','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22358','SV-38738r1_rule','default','medium','All skeleton files (typically in /etc/skel) must be group-owned by security.','"If the skeleton files are not protected; unauthorized personnel could change user start-up parameters and possibly jeopardize user files."','F-32453r1_fix','"Change the group owner of the skeleton file to security.Procedure: chgrp security /etc/security/.profile /etc/security/mkuser.sys"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001830','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-12002','SV-38948r1_rule','default','medium','The system must not forward IPv4 source-routed packets.','"Source-routed packets allow the source of the packet to suggest routers forward the packet along a different path than configured on the router; which can be used to bypass network security measures.  This requirement applies only to the forwarding of source-routed traffic; such as when IPv4 forwarding is enabled and the system is functioning as a router."','F-32490r1_fix','" /usr/sbin/no -po ""ipsrcrouteforward=0"""','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003600','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-4368','SV-27397r1_rule','default','medium','"The at.deny file must be owned by root; bin; or sys."','"If the owner of the at.deny file is not set to root; bin; or sys; unauthorized users could be allowed to view or edit sensitive information contained within the file."','F-24645r1_fix','"Change the owner of the at.deny file. chown root /var/adm/cron/at.deny"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003480','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22339','SV-38727r1_rule','default','medium','"The /etc/security/passwd file must be group-owned by security; bin; sys; or system."','The /etc/security/passwd file contains the list of local system accounts.  It is vital to system security and must be protected from unauthorized modification.  The file also contains password hashes which must not be accessible to users other than root.','F-32296r1_fix','"Change the group owner of the /etc/security/passwd file to security; bin; sys; or system.Procedure: chgrp security /etc/security/passwd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001410','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22335','SV-26431r1_rule','default','medium','The /etc/group file must be owned by root.','The /etc/group file is critical to system security and must be owned by a privileged user.  The group file contains a list of system groups and associated information.','F-23621r1_fix','"Change the owner of the /etc/group file to root. chown root /etc/group"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001391','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22336','SV-38725r1_rule','default','medium','"The /etc/group file must be group-owned by security; bin; sys; or system."','The /etc/group file is critical to system security and must be protected from unauthorized modification.  The group file contains a list of system groups and associated information.','F-32279r1_fix','"Change the group owner of the /etc/group file to security; bin; sys; or system. chgrp security /etc/group"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001392','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22337','SV-26433r1_rule','default','medium','The /etc/group file must have mode 0644 or less permissive.','The /etc/group file is critical to system security and must be protected from unauthorized modification.  The group file contains a list of system groups and associated information.','F-23623r1_fix','"Change the mode of the /etc/group file to 0644 or less permissive. chmod 0644 /etc/group"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001393','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22444','SV-39180r1_rule','default','medium','"The ftpusers file must be group-owned by bin; sys; or system."','"If the ftpusers file is not group-owned by a system group; an unauthorized user may modify the file to allow unauthorized accounts to use FTP."','F-33434r1_fix','"Change the group owner of the ftpusers file.Procedure: chgrp system /etc/ftpusers"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN004930','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22333','SV-38723r1_rule','default','medium','"The /etc/passwd file must be group-owned by bin; security; sys; or system."','The /etc/passwd file contains the list of local system accounts.  It is vital to system security and must be protected from unauthorized modification.','F-32272r1_fix','"Change the group owner of the /etc/passwd file to security; bin; sys; or system.Procedure: chgrp  security /etc/passwd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001379','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-4430','SV-27372r1_rule','default','medium','"The cron.deny file must be owned by root; bin; or sys."','"Cron daemon control files restrict the scheduling of automated tasks and must be protected."','F-24618r1_fix','chown root /var/adm/cron/cron.deny','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003260','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-4688','SV-38878r1_rule','default','high','The rexec daemon must not be running.','"The rexecd process provides a typically unencrypted; host-authenticated remote access service.  SSH should be used in place of this service."','F-33130r1_fix','"Edit /etc/inetd.conf and comment out the line for the rexec service.  Refresh the inetd daemon. refresh -s inetd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003840','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22308','SV-38680r1_rule','default','low','The system must restrict the ability to switch to the root user to members of a defined group.','"Configuring a supplemental group for users permitted to switch to the root user prevents unauthorized users from accessing the root account; even with knowledge of the root credentials."','F-32099r1_fix','"Use the chsec command to only allow users in the adm group to su to root.chsec -f /etc/security/user -s root -a sugroups=adm"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000850','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-11973','SV-39503r1_rule','default','medium','The system must require that passwords contain at least one special character.','"To enforce the use of complex passwords; minimum numbers of characters of different classes are mandated. The use of complex passwords reduces the ability of attackers to successfully obtain valid passwords using guessing or exhaustive search techniques. Complexity requirements increase the password search space by requiring users to construct passwords from a larger character set than they may otherwise use."','F-33711r1_fix','"Use the chsec command to set the minother setting to 1. chsec -f /etc/security/user -s default -a minother=1 chuser minother=1 < user id >"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000640','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-824','SV-824r2_rule','default','medium','The services file must have mode 0444 or less permissive.','The services file is critical to the proper operation of network services and must be protected from unauthorized modification.  Unauthorized modification could result in the failure of network services.','F-978r2_fix','"Change the mode of the services file to 0444 or less permissive.Procedure: chmod 0444 /etc/services"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003780','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22302','SV-38769r1_rule','default','medium','The system must enforce the entire password during authentication.','"Some common password hashing schemes only process the first eight characters of a users password; which reduces the effective strength of the password."','F-33345r1_fix','"Configure the system to enforce the correctness of the entire password during authentication.Configure the system to use sha password hashing.chsec -f /etc/security/login.cfg -s usw -a pwd_algorithm=ssha256"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000585','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22304','SV-38672r1_rule','default','medium','The password hashes stored on the system must have been generated using a FIPS 140-2 approved cryptographic hashing algorithm.','Systems must employ cryptographic hashes for passwords using the SHA-2 family of algorithms or FIPS 140-2 approved successors. The use of unapproved algorithms may result in weak password hashes that are more vulnerable to compromise.','F-32015r1_fix','"Change the passwords for all accounts using non-compliant password hashes.  passwd accountOR smitty passwd(This requires that GEN000590 is already met.)"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000595','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22307','SV-38678r1_rule','default','medium','The system must prevent the use of dictionary words for passwords.','"An easily guessable password provides an open door to any external or internal malicious intruder. Many computer compromises occur as the result of account name and password guessing. This is generally done by someone with an automated script using repeated logon attempts until the correct account and password pair is guessed. Utilities; such as cracklib; can be used to validate that passwords are not dictionary words and meet other criteria during password changes."','F-32089r1_fix','"Install the default dictionary of words from the bos.data fileset with smitty or installp. smitty installpinstallp bos.data Customize or modify the dictionary in /usr/share/dict/words as necessary.vi /usr/share/dict/wordsAdd a dictionary list to /etc/security/user file with the chsec command.chsec -f /etc/security/user -s default -a dictionlist=/usr/share/dict/words"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000790','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22306','SV-38677r2_rule','default','medium','The system must require at least eight characters be changed between the old and new passwords during a password change.','"To ensure password changes are effective in their goals; the system must ensure old and new passwords have significant differences. Without significant changes; new passwords may be easily guessed based on the value of a previously compromised password."','F-32075r2_fix','"Use the chsec command to change mindiff to 8. chsec -f /etc/security/user -s default -a mindiff=8 chuser mindiff=8 < user id >"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000750','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22438','SV-40683r1_rule','default','medium','"The aliases file must be group-owned by sys; bin; or system."','"If the alias file is not group-owned by a system group; an unauthorized user may modify the file to add aliases to run malicious code or redirect e-mail."','F-34538r1_fix','"Change the group owner of the /etc/mail/aliases file.Procedure: chgrp system /etc/mail/aliases"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN004370','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22435','SV-39875r1_rule','default','medium','"The hosts.lpd (or equivalent) file must be group-owned by bin; sys; or system."','"Failure to give group ownership of  the hosts.lpd file to bin; sys; or system provides the members of the owning group and possible unauthorized users; with the potential to modify the hosts.lpd file.  Unauthorized modifications could disrupt access to local printers from authorized remote hosts or permit unauthorized remote access to local printers."','F-34022r1_fix','"Change the group owner of the hosts.lpd file.Procedure: chgrp sys /etc/hosts.lpd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003930','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22432','SV-38876r1_rule','default','medium','The rlogind service must not be running.','"The rlogind process provides a typically unencrypted; host-authenticated remote access service.  SSH should be used in place of this service."','F-33129r1_fix','"Disable the rlogind service out of the /etc/inetd.conf file. vi  /etc/inetd.conf Comment out the rlogind service. Restart the inetd service.   refresh -s inetd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003830','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22430','SV-38952r1_rule','default','medium','The portmap or rpcbind service must not be installed unless needed.','The portmap and rpcbind services increase the attack surface of the system and should only be used when needed.  The portmap or rpcbind services are used by a variety of services using Remote Procedure Calls (RPCs).','F-31836r1_fix','"If the portmap or rpcbind service is part of a removable package; consult vendor documentation for the procedure to remove the package. If the service cannot be removed; prevent service activation by removing all permissions from the executable.  Procedure: chmod 0000 /usr/sbin/portmap"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003815','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-825','SV-38893r1_rule','default','low','Global initialization files must contain the mesg -n or mesg n commands.','"If the mesg -n or mesg n command is not placed into the system profile; messaging can be used to cause a Denial of Service attack."','F-979r2_fix','Edit /etc/profile or another global initialization script and add the mesg -n command.','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001780','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-11988','SV-12489r2_rule','default','high','"There must be no .rhosts; .shosts; hosts.equiv; or shosts.equiv files on the system."','"The .rhosts; .shosts; hosts.equiv; and shosts.equiv files are used to configure host-based authentication for individual users or the system.  Host-based authentication is not sufficient for preventing unauthorized access to the system."','F-11249r2_fix','"Remove the .rhosts; .shosts; hosts.equiv; and/or shosts.equiv files."','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN002040','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-840','SV-28403r1_rule','default','medium','The ftpusers file must exist.','"The ftpusers file contains a list of accounts not allowed to use FTP to transfer files. If this file does not exist; then unauthorized accounts can utilize FTP."','F-25674r1_fix','Create a /etc/ftpusers file containing a list of accounts not authorized for FTP.','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN004880','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-842','SV-28409r1_rule','default','medium','The ftpusers file must be owned by root.','"If the file ftpusers is not owned by root; an unauthorized user may modify the file to allow unauthorized accounts to use FTP."','F-25694r1_fix','"Change the owner of the ftpusers file to root. chown root /etc/ftpusers"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN004920','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-843','SV-28412r1_rule','default','medium','The ftpusers file must have mode 0640 or less permissive.','Excessive permissions on the ftpusers file could permit unauthorized modification.  Unauthorized modification could result in Denial of Service to authorized FTP users or permit unauthorized users to access the FTP service.','F-25697r1_fix','"Change the mode of the ftpusers file to 0640. chmod 0640 /etc/ftpusers"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN004940','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-11981','SV-38882r1_rule','default','medium','All global initialization files must have mode 0644 or less permissive.','Global initialization files are used to configure the users shell environment upon login.  Malicious modification of these files could compromise accounts upon logon.','F-11242r2_fix','"Change the mode of the global initialization file(s) to 0444. chmod 0444 <global initialization file>"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001720','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-4295','SV-40862r2_rule','default','high','The SSH daemon must be configured to only use the SSHv2 protocol.','SSHv1 is not a DoD-approved protocol and has many well-known vulnerability exploits.  Exploits of the SSH daemon could provide immediate root access to the system.','F-4206r2_fix','"Edit the configuration file and modify the Protocol line.Protocol 2Restart sshd:/sbin/init.d/secsh stop/sbin/init.d/secsh start"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN005500','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-11983','SV-38892r1_rule','default','medium','"All global initialization files must be group-owned by sys; bin; system; or security."','Global initialization files are used to configure the users shell environment upon login.  Malicious modification of these files could compromise accounts upon logon.  Failure to give ownership of sensitive files or utilities to root or bin provides the designated owner and unauthorized users with the potential to access sensitive information or change the system configuration which could weaken the systems security posture.','F-33353r1_fix','"Change the group ownership of the global initialization file(s) with incorrect group ownership.Procedure: chgrp system <global initialization file>"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001760','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-11982','SV-38884r1_rule','default','medium','All global initialization files must be owned by root.','Global initialization files are used to configure the users shell environment upon login.  Malicious modification of these files could compromise accounts upon logon.  Failure to give ownership of sensitive files or utilities to root or bin provides the designated owner and unauthorized users with the potential to access sensitive information or change the system configuration which could weaken the systems security posture.','F-11243r2_fix','"Change the ownership of global initialization files with incorrect ownership.Procedure: chown bin <global initialization files>"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001740','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29520','SV-38751r1_rule','default','medium','The /etc/ftpaccess.ctl file must be owned by root.','"If the ftpaccess.ctl  file is not owned by root; an unauthorized user may modify the file to allow unauthorized access to change the file.   Unauthorized modification could result in Denial of Service to authorized FTP users or permit unauthorized access to system information."','F-33078r1_fix','"Change the owner of the ftpaccess.ctl file to root. chown root /etc/ftpaccess.ctl"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000000-AIX0320','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-922','SV-38846r1_rule','default','high','All shell files must have mode 0755 or less permissive.','Shells with world/group write permissions give the ability to maliciously modify the shell to obtain unauthorized access.','F-33102r1_fix','"Change the mode of the shell. chmod 0755 < shell >"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN002220','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29522','SV-38753r1_rule','default','medium','The /etc/ftpaccess.ctl file must have mode 0640 or less permissive.','Excessive permissions on the ftpaccess.ctl file could permit unauthorized modification. Unauthorized modification could result in Denial of Service to authorized FTP users or permit unauthorized access to system information.','F-33080r1_fix','"Change the mode of the /etc/ftpaccess.ctl file to 0640. chmod 0640 /etc/ftpaccess.ctl"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000000-AIX0340','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29495','SV-38699r1_rule','default','medium','The system must not allow directed broadcasts to gateway.','Disabling directed broadcast prevents packets directed to a gateway to be broadcasted on a remote network.','F-33053r1_fix','"Configure directed_broadcast  to 0. /usr/sbin/no -p -o directed_broadcast=0"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000000-AIX0200','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-12049','SV-38880r1_rule','default','medium','Network analysis tools must not be installed.','Network analysis tools allow for the capture of network traffic visible to the system.','F-33131r1_fix','"Remove the network analysis tool binary from the system. Procedure: rm /usr/sbin/tcpdump"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003865','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-4358','SV-27360r1_rule','default','medium','The cron.deny file must have mode 0600 or less permissive.','"If file permissions for cron.deny are more permissive than 0600; sensitive information could be viewed or edited by unauthorized users."','F-24606r1_fix','"Change the mode of the cron.deny file. chmod 0600 /var/adm/cron/cron.deny"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003200','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22549','SV-38963r1_rule','default','medium','The DHCP client must not send dynamic DNS updates.','Dynamic DNS updates transmit unencrypted information about a system including its name and address and should not be used unless needed.','F-32346r1_fix','"Configure the systems DHCP client to not send dynamic DNS updates.  Remove / comment updateDNS lines from the /etc/dhcpcd.ini and /etc/dhcpc.opt files."','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN007850','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22548','SV-38931r1_rule','default','medium','The DHCP client must be disabled if not needed.','DHCP allows for the unauthenticated configuration of network parameters on the system by exchanging information with a DHCP server.','F-33172r1_fix','"Disable the systems DHCP client. Edit /etc/rc.tcpip; comment out the line starting dhcpcd.  Reboot the system to ensure the DHCP client has been disabled fully.  Configure a static IP for the system; if network connectivity is required."','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN007840','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29496','SV-38700r1_rule','default','medium','The system must provide protection from Internet Control Message Protocol (ICMP) attacks on TCP connections.','"The ICMP attacks may be of the form of ICMP source quench attacks and Path MTU Discovery (PMTUD) attacks. If this network option tcp_icmpsecure is turned on; the system does not react to ICMP source quench messages. This will protect against ICMP source quench attacks.  The payload of the ICMP message is tested to determine if the sequence number of the TCP header portion of the payload is within the range of acceptable sequence numbers. This will mitigate PMTUD attacks to a large extent.  "','F-33054r1_fix','"Set the tcp_icmpsecure parameter to 1.  /usr/sbin/no -p -o tcp_icmpsecure=1"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000000-AIX0210','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29497','SV-38701r1_rule','default','medium','"The system must provide protection for the TCP stack against connection resets; SYN; and data injection attacks."','"The tcp_tcpsecure parameter provides protection for TCP connections from fake SYNs; fake RST; and data injections on established connections.  The first vulnerability involves sending a fake SYN to an established connection to abort the connection. The second vulnerability involves sending a fake RST to an established connection to abort the connection. The third vulnerability involves injecting fake data in an established TCP connection."','F-33055r1_fix','"Set the tcp_tcpsecure parameter to 7. /usr/sbin/no -p -o tcp_tcpsecure=7"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000000-AIX0220','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1059','SV-40725r1_rule','default','medium','The /var/private/smbpasswd file must have mode 0600 or less permissive.','"If the smbpasswd file has a mode more permissive than 0600; the smbpasswd file may be maliciously accessed or modified; potentially resulting in the compromise of Samba accounts."','F-34585r1_fix','"Change the mode of the smbpasswd file to 0600.Procedure: chmod 0600 /var/private/smbpasswd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN006200','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1058','SV-39235r1_rule','default','medium','The /var/private/smbpasswd file must be group-owned by sys or system.','"If the smbpasswd file is not group-owned by root; the smbpasswd file may be maliciously accessed or modified; potentially resulting in the compromise of Samba accounts."','F-33485r1_fix','"Use the chgrp command to change  the group owner of the smbpasswd file to system.  chgrp system /var/private/smbpasswd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN006180','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22398','SV-39356r1_rule','default','medium','"The at.deny file must be group-owned by system; bin; sys; or cron."','"If the group owner of the at.deny file is not set to system; bin; sys; or cron; unauthorized users could be allowed to view or edit sensitive information contained within the file.  Unauthorized modification could result in Denial of Service to authorized at users or provide unauthorized users with the ability to run at jobs."','F-33590r1_fix','"Change the group owner of the at.deny file to bin; sys; system; or cron.Procedure:  chgrp cron /var/adm/cron/at.deny"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003490','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22396','SV-39352r1_rule','default','medium','"The ""at"" directory must be group-owned by system; bin; sys; or cron."','"If the group of the ""at"" directory is not system; bin; sys; or cron; unauthorized users could be allowed to view or edit files containing sensitive information within the directory."','F-33587r1_fix','"Change the group ownership of the file to bin; sys; system; or cron.Procedure: chgrp cron /var/spool/cron/atjobs"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003430','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22397','SV-39354r1_rule','default','medium','"The at.allow file must be group-owned by system; bin; sys; or cron."','"If the group-owner of the at.allow file is not set to system; bin; sys; or cron; unauthorized users could be allowed to view or edit the list of users permitted to run at jobs.  Unauthorized modification could result in Denial of Service to authorized at users or provide unauthorized users with the ability to run at jobs."','F-33589r1_fix','"Change the group owner of the at.allow file to sys; system; bin; or cron. Procedure:  chgrp cron /var/adm/cron/at.allow"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003470','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22394','SV-38789r1_rule','default','medium','"The cron.deny file must be group-owned by system; bin; sys; or cron."','Cron daemon control files restrict the scheduling of automated tasks and must be protected.  Unauthorized modification of the cron.deny file could result in Denial of Service to authorized cron users or could provide unauthorized users with the ability to run cron jobs.','F-32480r1_fix','"Change the group owner of the cron.deny file to sys; system; bin; or cron.Procedure: chgrp cron /var/adm/cron/cron.deny"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003270','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1056','SV-39231r1_rule','default','medium','"The /usr/lib/smb.conf file must be group-owned by bin; sys; or system."','"If the group-owner of the smb.conf file is not root or a system group; the file may be maliciously modified and the Samba configuration could be compromised."','F-33481r1_fix','"Change the group owner of the smb.conf file. Procedure:  chgrp system /usr/lib/smb.conf"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN006120','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22392','SV-38787r1_rule','default','medium','The at.deny file must have mode 0640 or less permissive.','The at daemon control files restrict access to scheduled job manipulation and must be protected. Unauthorized modification of the at.deny file could result in Denial of Service to authorized at users or provide unauthorized users with the ability to run at jobs.','F-32478r1_fix','"Change the mode of the at.deny file to 0640. chmod 0640 /var/adm/cron/at.deny"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003252','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22423','SV-40385r1_rule','default','medium','"The inetd.conf file; xinetd.conf file; and the xinetd.d directory must be group-owned by bin; sys; or system."','Failure to give ownership of sensitive files or utilities to system groups may provide unauthorized users with the potential to access sensitive information or change the system configuration which could weaken the systems security posture.','F-34362r1_fix','"Change the group ownership of the inetd configuration file.Procedure: chgrp system /etc/inetd.conf"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003730','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-11996','SV-27402r1_rule','default','low','Process core dumps must be disabled unless needed.','"Process core dumps contain the memory in use by the process when it crashed.  Process core dump files can be of significant size and their use can result in file systems filling to capacity; which may result in Denial of Service.  Process core dumps can be useful for software debugging.  "','F-24650r1_fix','chsec -f /etc/security/limits -s default -a core=0','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003500','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-822','SV-822r2_rule','default','medium','The inetd.conf and xinetd.conf files must have mode 0440 or less permissive.','The Internet service daemon configuration files must be protected as malicious modification could cause Denial of Service or increase the attack surface of the system.','F-976r2_fix','"Change the mode of the inetd.conf file. chmod 0440 /etc/inetd.conf"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003740','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22427','SV-39112r1_rule','default','medium','"The services file must be group-owned by bin; sys; or system."','Failure to give ownership of system configuration files to root or a system group provides the designated owner and unauthorized users with the potential to change the system configuration which could weaken the systems security posture.','F-33378r1_fix','"Change the group owner of the services file.Procedure: chgrp system /etc/services"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003770','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22475','SV-40716r1_rule','default','low','The SSH daemon must not permit Kerberos authentication unless needed.','"Kerberos authentication for SSH is often implemented using GSSAPI.  If Kerberos is enabled through SSH; the SSH daemon provides a means of access to the systems Kerberos implementation.  Vulnerabilities in the systems Kerberos implementation may then be subject to exploitation.  To reduce the attack surface of the system; the Kerberos authentication mechanism within SSH must be disabled for systems not using this capability."','F-34575r1_fix','"Edit the /etc/ssh/sshd_config file and remove the KerberosAuthentication setting or change the value of the setting to ""no""."','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN005526','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-823','SV-823r2_rule','default','medium','The services file must be owned by root or bin.','Failure to give ownership of sensitive files or utilities to root or bin provides the designated owner and unauthorized users with the potential to access sensitive information or change the system configuration which could weaken the systems security posture.','F-977r2_fix','"Change the ownership of the services file to root or bin.Procedure: chown root /etc/services"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003760','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-4298','SV-27149r1_rule','default','medium','Remote consoles must be disabled or protected from unauthorized access.','"The remote console feature provides an additional means of access to the system which could allow unauthorized access if not disabled or properly secured.  With virtualization technologies; remote console access is essential as there is no physical console for virtual machines.  Remote console access must be protected in the same manner as any other remote privileged access method."','F-24423r1_fix','Edit /etc/security/login.cfg and remove the alternate console definition.','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001000','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29493','SV-38697r1_rule','default','medium','The /etc/netsvc.conf file must have mode 0644 or less permissive.','"The /etc/netsvc.conf file is used to specify the ordering of name resolution for the sendmail command;  alias resolution for the sendmail command; and host name resolution routines.    Malicious changes could prevent the system from functioning correctly or compromise system security."','F-33051r1_fix','"Change the mode of the /etc/netsvc.conf file to 0644 or less permissive. chmod 0644 /etc/netsvc.conf"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000000-AIX0100','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22296','SV-40384r1_rule','default','medium','The time synchronization configuration file (such as /etc/ntp.conf) must have mode 0640 or less permissive.','"A synchronized system clock is critical for the enforcement of time-based policies and the correlation of logs and audit records with other systems.  If an illicit time source is used for synchronization; the integrity of system logs and the security of the system could be compromised.  If the configuration files controlling time synchronization are not protected; unauthorized modifications could result in the failure of time synchronization."','F-34357r1_fix','"Change the mode of the ntp.conf file to 0640 or less permissive. chmod 0640 /etc/ntp.conf"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000252','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-787','SV-787r2_rule','default','medium','System log files must have mode 0640 or less permissive.','"If the system log files are not protected; unauthorized users could change the logged data; eliminating its forensic value."','F-941r2_fix','"Change the mode of the system log file(s) to 0640 or less permissive.Procedure: chmod 0640 /path/to/system-log-fileNOTE: Do not confuse system log files with audit logs.   Any subsystems that require less stringent permissions must be documented."','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001260','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-832','SV-40684r1_rule','default','medium','The alias file must have mode 0644 or less permissive.','"Excessive permissions on the aliases file may permit unauthorized modification.  If the alias file is modified by an unauthorized user; they may modify the file to run malicious code or redirect email."','F-34539r1_fix','"Change the mode of the /etc/mail/aliases file.Procedure: chmod 0644 /etc/mail/aliases"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN004380','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22295','SV-39093r1_rule','default','medium','"The time synchronization configuration file (such as /etc/ntp.conf) must be group-owned by bin; sys; or system."','"A synchronized system clock is critical for the enforcement of time-based policies and the correlation of logs and audit records with other systems.  If an illicit time source is used for synchronization; the integrity of system logs and the security of the system could be compromised.  If the configuration files controlling time synchronization are not owned by a system group; unauthorized modifications could result in the failure of time synchronization."','F-33334r1_fix','"Change the group owner of the NTP configuration file.Procedure: chgrp system /etc/ntp.conf"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000251','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-781','SV-27071r1_rule','default','low','All Group Identifiers (GIDs) referenced in the /etc/passwd file must be defined in the /etc/group file.','"If a user is assigned the GID of a group that does not exist on the system; and a group with that GID is subsequently created; the user may have unintended rights to the group."','F-33340r1_fix','"Add a group to the system for each GID referenced without a corresponding group.   smitty mkgroup"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000380','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29511','SV-38715r1_rule','default','medium','The system must not have the netstat service active on the inetd process.','The netstat service can potentially give out network information on active connections if it is running.  The information given out can aid in an attack and weaken the systems defensive posture.','F-33069r1_fix','"Edit /etc/inetd.conf and comment out the netstat service line. Restart the inetd service.    refresh -s inetd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN009270','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29510','SV-38714r1_rule','default','medium','The system must not have the talk or ntalk services active.','The talk and ntalk commands allow users on the same or different systems on converse. The talk daemons are started from the inetd process and run as root.  These unnecessary processes increase the attack vector of the system  and may cause Denial of Service by scrambling the users display.','F-33068r1_fix','"Edit /etc/inetd.conf and comment out TCP and UDP for the talk service.   Edit /etc/inetd.conf and comment out TCP and UDP for the ntalk service.Restart the inetd service.    refresh -s inetd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN009260','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29513','SV-38717r1_rule','default','medium','The system must not have the systat service active.','The systat daemon allows remote users to see the running process and who is running them.  This may aid in information collection for an attack and weaken the security posture of the system.','F-33071r1_fix','"Edit /etc/inetd.conf and comment out systat the service line. Restart the inetd service.    refresh -s inetd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN009290','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29512','SV-38716r1_rule','default','medium','The system must not have the PCNFS service active.','"The PCNFS service predates Microsoft’s SMB specifications.   If a similar service is needed to share files from a Windows based OS to a UNIX based OS;  consider SAMBA."','F-33070r1_fix','"Edit /etc/inetd.conf and comment out the PCNFS service line. Restart the inetd service.    refresh -s inetd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN009280','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-986','SV-27385r1_rule','default','medium','Default system accounts (with the exception of root) must not be listed in the at.allow file or must be included in the at.deny file if the at.allow file does not exist.','"Default accounts; such as bin; sys; adm; uucp; daemon; and others; should never have access to the at facility.  This would create a possible vulnerability open to intruders or malicious users."','F-1140r2_fix','"Remove the default accounts (such as bin; sys; adm; and others) from the at.allow file."','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003320','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29514','SV-38718r1_rule','default','medium','The inetd time service must not be active on the system on the inetd daemon.','The time service is an internal inetd function is used by the rdate command.  This service is sometimes used to synchronize clocks at boot time.   The service is outdated.   Use the ntpdate command instead.','F-33072r1_fix','"Edit the /etc/inetd.conf file and comment out the time service line. Restart the inetd service.    refresh -s inetd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN009300','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29517','SV-38721r1_rule','default','medium','The system must not have the rstatd service active.','"The rstatd can give out information on the running system; such as  the CPU usage;  the system uptime; its network usage; and other system information that could potentially aid in an attack.  The rstatd service is unnecessary and it weakens the defensive posture of the system.  If systems monitoring is needed;  use a third party tool or SNMP.   "','F-33075r1_fix','"Edit the /etc/inetd.conf file and comment out the rstatd service line. Restart the inetd service.    refresh -s inetd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN009330','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29516','SV-38720r1_rule','default','medium','The system must not have the sprayd service active.','The sprayd service is sometimes used for network and nfs troubleshooting.  The spray service can be used for both buffer overflow and Denial of Service attacks by saturating the network.  The sprayd daemon is an unnecessary service.','F-33074r1_fix','"Edit the /etc/inetd.conf file and comment out the sprayd service line. Restart the inetd service.    refresh -s inetd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN009320','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22550','SV-38825r1_rule','default','medium','The system must ignore IPv6 ICMP redirect messages.','ICMP redirect messages are used by routers to inform hosts that a more direct route exists for a particular destination. These messages modify the hosts route table and are unauthenticated. An illicit ICMP redirect message could result in a man-in-the-middle attack.','F-32348r1_fix','"Configure the system to ignore IPv6 ICMP redirect messages. /usr/sbin/no -p -o ipignoreredirects=1"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN007860','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22551','SV-38826r1_rule','default','medium','The system must not send IPv6 ICMP redirects.','ICMP redirect messages are used by routers to inform hosts that a more direct route exists for a particular destination. These messages contain information from the systems route table that could reveal portions of the network topology.','F-32349r1_fix','"Configure the system to not send IPv6 ICMP redirects.   /usr/sbin/no -p -o ipsendredirects=0"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN007880','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-928','SV-28445r1_rule','default','medium','The NFS export configuration file must be owned by root.','Failure to give ownership of the NFS export configuration file to root provides the designated owner and possible unauthorized users with the potential to change system configuration which could weaken the systems security posture.','F-25756r1_fix','"Change the owner of the exports file to root.Example: chown root /etc/exports"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN005740','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22554','SV-38828r1_rule','default','medium','The system must not accept source-routed IPv6 packets.','"Source-routed packets allow the source of the packet to suggest routers forward the packet along a different path than configured on the router; which can be used to bypass network security measures. This requirement applies only to the handling of source-routed traffic destined to the system itself; not to traffic forwarded by the system to another; such as when IPv6 forwarding is enabled and the system is functioning as a router."','F-32352r1_fix','"Configure the system to not accept source-routed IPv6 packets.  /usr/sbin/no -p -o ipsrcrouterecv=0"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN007940','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-4369','SV-28393r1_rule','default','medium','The traceroute command owner must be root.','"If the traceroute command owner has not been set to root; an unauthorized user could use this command to obtain knowledge of the network topology inside the firewall.  This information may allow an attacker to determine trusted routers and other network information possibly leading to system and network compromise."','F-25665r1_fix','"Change the owner of the traceroute command to root.Example: chown root /usr/bin/traceroute"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003960','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29521','SV-38752r1_rule','default','medium','"The /etc/ftpaccess.ctl file must be group-owned by bin; sys; or system."','"If the ftpaccess.ctl file is not group-owned by a system group; an unauthorized user may modify the file to allow unauthorized access to modify the file. Unauthorized modification could result in Denial of Service to authorized FTP users or permit unauthorized access to system information."','F-33079r1_fix','"Change the group owner of the /etc/ftpaccess.ctl file. chgrp system /etc/ftpaccess.ctl"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000000-AIX0330','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-4364','SV-38907r1_rule','default','medium','The at directory must have mode 0755 or less permissive.','"If the at directory has a mode more permissive than 0755; unauthorized users could be allowed to view or to edit files containing sensitive information within the at directory.  Unauthorized modifications could result in Denial of Service to authorized at jobs."','F-4275r2_fix','"Change the mode of the ""at"" directory to 0755.Procedure: chmod 0755 < at directory >"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003400','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22559','SV-38969r1_rule','default','medium','If the system is using LDAP for authentication or account information the /etc/ldap.conf (or equivalent) file must have mode 0644 or less permissive.','"LDAP can be used to provide user authentication and account information; which are vital to system security.  The LDAP client configuration must be protected from unauthorized modification."','F-33178r1_fix','"Change the permissions of the /etc/security/ldap/ldap.cfg file to 0644 or less permissive. chmod 0644 /etc/security/ldap/ldap.cfg"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN008060','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22311','SV-38772r1_rule','default','medium','The root accounts list of preloaded libraries must be empty.','"The library preload list environment variable contains a list of libraries for the dynamic linker to load before loading the libraries required by the binary.  If this list contains paths to libraries relative to the current working directory; unintended libraries may be preloaded.  This variable is formatted as a space-separated list of libraries.  Paths starting with (/) are absolute paths."','F-33095r1_fix','Edit the root users initialization files and remove any definition of LDR_PRELOAD.','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000950','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-4361','SV-27367r1_rule','default','medium','"The cron.allow file must be owned by root; bin; or sys."','"If the owner of the cron.allow file is not set to root; bin; or sys; the possibility exists for an unauthorized user to view or to edit sensitive information."','F-24612r1_fix','chown root /var/adm/cron/cron.allow','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003240','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-11976','SV-38939r1_rule','default','medium','User passwords must be changed at least every 60 days.','Limiting the lifespan of authenticators limits the period of time an unauthorized user has access to the system while using compromised credentials and reduces the period of time available for password-guessing attacks to run against a single password.','F-32059r1_fix','"Use the chsec command to set the maxage field to 8 for each user. chsec -f /etc/security/user -s default -a maxage=8 chuser maxage=8 < user id >"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000700','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29499','SV-38703r1_rule','default','medium','The system must not have the bootp service active.','The bootp service is used for Network Installation Management (NIM) and remote booting of systems.  The bootp service should not be active unless it is needed for NIM servers or booting remote systems.  Running unnecessary services increases the attack vector of the system.','F-33057r1_fix','"Disable the bootp service from /etc/inetd.conf.Edit /etc/inetd.conf and comment out bootp service line. Restart the inetd service.   refresh -s inetd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000000-AIX0300','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22419','SV-38803r1_rule','default','medium','The system must be configured to use TCP syncookies when experiencing a TCP SYN flood.','"A TCP SYN flood attack can cause Denial of Service by filling a systems TCP connection table with connections in the SYN_RCVD state.  Syncookies are a mechanism used to not track a connection until a subsequent ACK is received; verifying the initiator is attempting a valid connection and is not a flood source.  This technique does not operate in a fully standards-compliant manner; but is only activated when a flood condition is detected; and allows defense of the system while continuing to service valid requests."','F-32500r1_fix','/usr/sbin/no -p -o clean_partial_conns=1','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003612','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-798','SV-798r2_rule','default','medium','The /etc/passwd file must have mode 0644 or less permissive.','"If the password file is writable by a group owner or the world; the risk of password file compromise is increased.  The password file contains the list of accounts on the system and associated information."','F-952r2_fix','"Change the mode of the passwd file to 0644.Procedure: chmod 0644 /etc/passwdDocument all changes."','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001380','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29519','SV-38750r1_rule','default','medium','The /etc/ftpaccess.ctl file must exist.','"The ftpaccess.ctl  file contains options for the ftp daemon; such as herald; motd; user access;  and permissions to files and directories. If the ftpaccess.ctl file does not exist; the ftpd process will not display any warning banners; and permissions will only be enforced using basic UNIX permissions. "','F-33077r1_fix','"Create a /etc/ftpaccess.ctl file.touch /etc/ftpaccess.ctlAdd at least the herald: /path to login banner to the /etc/ftpaccess.ctl file.vi /etc/ftpaccess.ctl"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000000-AIX0310','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22413','SV-38949r1_rule','default','medium','The system must prevent local applications from generating source-routed packets.','"Source-routed packets allow the source of the packet to suggest routers forward the packet along a different path than configured on the router; which can be used to bypass network security measures."','F-32496r1_fix','" /usr/sbin/no -po ""ipsrcroutesend=0"""','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003606','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22414','SV-38800r1_rule','default','medium','The system must not accept source-routed IPv4 packets.','"Source-routed packets allow the source of the packet to suggest routers forward the packet along a different path than configured on the router; which can be used to bypass network security measures. This requirement applies only to the handling of source-routed traffic destined to the system itself; not to traffic forwarded by the system to another; such as when IPv4 forwarding is enabled and the system is functioning as a router"','F-32497r1_fix','"Configure the system to not accept source-routed IPv4 packets.  /usr/sbin/no -p -o ipsrcrouterecv=0"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003607','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22417','SV-38802r1_rule','default','medium','The system must not send IPv4 ICMP redirects.','ICMP redirect messages are used by routers to inform hosts a more direct route exists for a particular destination.  These messages contain information from the systems route table possibly revealing  portions of the network topology.','F-32499r1_fix','/usr/sbin/no  -p -o ipsendredirects=0','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003610','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22416','SV-38801r1_rule','default','medium','The system must ignore IPv4 ICMP redirect messages.','"ICMP redirect messages are used by routers to inform hosts a more direct route exists for a particular destination. These messages modify the hosts route table and are unauthenticated. An illicit ICMP redirect message could result in a man-in-the-middle attack."','F-32498r1_fix','"Configure the system to ignore IPv4 ICMP redirect messages. /usr/sbin/no -p -o ipignoreredirects=1"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003609','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29506','SV-38710r1_rule','default','medium','The system must not have the dtspc service active.','This service is started automatically by the inetd daemon with root permission in response to a CDE client requesting a process to be started on the daemon’s host system.   Running the dtscp service is unnecessary and it increases the attack vector of the system.','F-33064r1_fix','"Edit /etc/inetd.conf and comment out dtspc service line. Restart the inetd service.    refresh -s inetd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN009220','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29507','SV-38711r1_rule','default','medium','The system must not have the echo service active.','The echo service can be used in Denial of Service or SMURF attacks.  It can also used at someone else to get through a firewall or start a data storm.  The echo service is unnecessary and it increases the attack vector of the system.','F-33065r1_fix','"Edit /etc/inetd.conf and comment out the echo service lines for both TCP and UDP. Restart the inetd service.    refresh -s inetd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN009230','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29504','SV-38708r1_rule','default','medium','The system must not have the daytime service active.','The daytime service runs as root from the inetd daemon and can provide an opportunity for Denial of Service PING or PING-PONG attacks.   The daytime service is unnecessary and it increases the attack vector of the system.','F-33062r1_fix','"Edit /etc/inetd.conf and comment out daytime service lines for both TCP and UDP protocols. Restart the inetd service.    refresh -s inetd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN009200','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29505','SV-38709r1_rule','default','medium','The system must not have the discard service active.','The discard service runs as root from the inetd server and can be used in Denial of Service attacks.   The discard  service is unnecessary and it increases the attack vector of the system.','F-33063r1_fix','"Edit /etc/inetd.conf and comment out the discard service line for both TCP and UDP protocols. Restart the inetd service.refresh -s inetd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN009210','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29502','SV-38706r1_rule','default','medium','The system must not have the tool-talk database server (ttdbserver) service active.','The ttdbserver service for CDE is an unnecessary service that runs as root and might be compromised.','F-33060r1_fix','"Edit /etc/inetd.conf and comment out ttdbserver service line. Restart the inetd service.    refresh -s inetd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN009180','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29503','SV-38707r1_rule','default','medium','The system must not have the comsat service active.','The comsat daemon notifies users on incoming email.  This is an unnecessary service and is vulnerable to a flood attack.  Running unnecessary services increases the attack vector of the system.','F-33061r1_fix','"Edit /etc/inetd.conf and comment out comsat service line. Restart the inetd service.    refresh -s inetd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN009190','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29500','SV-38704r1_rule','default','medium','The system must not have the chargen service active.','"When contacted; chargen responds with some random characters. When contacted via UDP; it will respond with a single UDP packet. When contacted via TCP; it will continue spewing characters until the client closes the connection. An easy attack is ping-pong in which an attacker spoofs a packet between two machines running chargen. This will cause them to spew characters at each other; slowing the machines down and saturating the network.  The chargen service is unnecessary and provides an opportunity for Denial of Service attack.  "','F-33058r1_fix','"Edit /etc/inetd.conf and comment out the chargen service line for both udp and tcp protocols.Restart the inetd service.   refresh -s inetd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN009140','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29501','SV-38705r1_rule','default','medium','The system must not have the Calendar Manager Service Daemon (CMSD) service active.','The CMSD service for CDE is an unnecessary process that  runs a root and increases attack vector of the system.  Buffer overflow attacks against the CMSD process can potentially give access to the system.','F-33059r1_fix','"Edit /etc/inetd.conf and comment out the CMSD service. Restart the inetd service.    refresh -s inetd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN009160','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-4284','SV-4284r2_rule','default','medium','The securetcpip command must be used.','"The AIX securetcpip command disables insecure network utilities; such as rcp; rlogin; rlogind; rsh; rshd; tftp; tftpd; and trpt/d. These services increase the attack surface of the system."','F-33317r1_fix','Ensure secure tcp/ip has been invoked before allowing operations on the system.','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000000-AIX00040','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29508','SV-38712r1_rule','default','medium','The system must not have Internet Message Access Protocol (IMAP) service active.','The IMAP service should not be running unless the system is acting as a mail server for client connections.   Running unnecessary services increases the attack vector on the system.','F-33066r1_fix','"Edit /etc/inetd.conf and comment out the imap2 service line. Restart the inetd service.    refresh -s inetd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN009240','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-29509','SV-38713r1_rule','default','medium','The system must not have the PostOffice Protocol (POP3) service active.','"The POP3 service is only needed if the server is acting as a mail server and clients are using applications that only support POP3.  Users ids and passwords are sent in plain text to the POP3 service.  If mail client access is needed;  consider using IMAP or SSL enabled POP3."','F-33067r1_fix','"Edit /etc/inetd.conf and comment out POP3 the service line. Restart the inetd service.    refresh -s inetd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN009250','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-790','SV-38776r1_rule','default','medium','"NIS/NIS+/yp files must be group-owned by sys; bin; other; or system."','"NIS/NIS+/yp files are part of the systems identification and authentication processes and are; therefore; critical to system security.  Failure to give ownership of sensitive files or utilities to root or bin provides the designated owner and unauthorized users with the potential to access sensitive information or change the system configuration which could weaken the systems security posture."','F-33349r1_fix','"Change the group owner of the NIS files to sys; bin; system; or other. Procedure:  chgrp system < directory>/< file >"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001340','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-791','SV-38781r1_rule','default','medium','The NIS/NIS+/yp files must have mode 0755 or less permissive.','"NIS/NIS+/yp files are part of the systems identification and authentication processes and are; therefore; critical to system security.  Unauthorized modification of these files could compromise these processes and the system."','F-945r2_fix','"Change the mode of NIS/NIS+/yp files to 0755 or less permissive.Procedure (example): chmod 0755 <filename>"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001360','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-821','SV-821r2_rule','default','medium','"The inetd.conf file; xinetd.conf file; and the xinetd.d directory must be owned by root or bin."','Failure to give ownership of sensitive files or utilities to root provides the designated owner and unauthorized users with the potential to access sensitive information or change the system configuration which could weaken the systems security posture.','F-975r2_fix','"Change the ownership of the inetd.conf file to root or bin.  Procedure: chown root /etc/inetd.conf"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003720','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-794','SV-794r4_rule','default','medium','All system command files must have mode 755 or less permissive.','Restricting permissions will protect system command files from unauthorized modification. System command files include files present in directories used by the operating system for storing default system executables and files present in directories included in the systems default executable search paths.','F-948r3_fix','"Change the mode for system command files to 755 or less permissive.Procedure: chmod 755 <filename>"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001200','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22310','SV-38770r1_rule','default','medium','The root accounts library search path must be the system default and must contain only absolute paths.','"The library search path environment variable(s) contains a list of directories for the dynamic linker to search to find libraries. If this path includes the current working directory or other relative paths; libraries in these directories may be loaded instead of system libraries. This variable is formatted as a colon-separated list of directories. If there is an empty entry; such as a leading or trailing colon or two consecutive colons; this is interpreted as the current working directory. Entries starting with a slash (/) are absolute paths."','F-33094r1_fix','Edit the root users initialization files and remove any definition of LD_LIBRARY_PATH and LIBPATH.','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN000945','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-797','SV-38944r1_rule','default','medium','The /etc/security/passwd file must be owned by root.','The /etc/security/passwd file contains the list of local system accounts.  It is vital to system security and must be protected from unauthorized modification.  Failure to give ownership of sensitive files or utilities to root or bin provides the designated owner and unauthorized users with the potential to access sensitive information or change the system configuration which could weaken the systems security posture.','F-32285r1_fix','"Change the ownership of the /etc/security/passwd file. chown root /etc/security/passwd"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001400','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-929','SV-28447r1_rule','default','low','The NFS export configuration file must have mode 0644 or less permissive.','"Excessive permissions on the NFS export configuration file could allow unauthorized modification of the file; which could result in Denial of Service to authorized NFS exports and the creation of additional unauthorized exports."','F-25760r1_fix','chmod 0644 /etc/exports','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN005760','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-4687','SV-27434r1_rule','default','high','The rsh daemon must not be running.','"The rshd process provides a typically unencrypted; host-authenticated remote access service.  SSH should be used in place of this service."','F-24706r1_fix','Edit /etc/inetd.conf and comment out the rshd service.  Restart the inetd service.','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN003820','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-22319','SV-26395r1_rule','default','medium','The /etc/resolv.conf file must be owned by root.','"The resolv.conf (or equivalent) file configures the systems DNS resolver.  DNS is used to resolve host names to IP addresses.  If DNS configuration is modified maliciously; host name resolution may fail or return incorrect information.  DNS may be used by a variety of system security functions; such as time synchronization; centralized authentication; and remote system logging."','F-23586r1_fix','"Change the owner of the /etc/resolv.conf file to root. chown root /etc/resolv.conf"','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN001362','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','','','default','','','','','','AIX 6.1 SECURITY TECHNICAL IMPLEMENTATION GUIDE',' III - Administrative Classified ','GEN006100','None','None','Imported data from UCF - DISA') from dual;
