select add_profile_data ('Windows Server 2012 - 2012 R2 Domain Controller' , ' III - Administrative Classified ','Windows Server OS','https://www.stigviewer.com/stig/windows_server_2012_2012_r2_domain_controller/2016-07-22/MAC-3_Classified/' , '2016-07-22' ,'Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1075','SV-52840r1_rule','default','low','The shutdown option must not be available from the logon dialog box.','Displaying the shutdown button may allow individuals to shut down a system anonymously.  Only authenticated users should be allowed to shut down the system.  Preventing display of this button in the logon dialog box ensures that individuals who shut down the system are authorized and tracked in the systems Security event log.','F-45766r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Shutdown: Allow system to be shutdown without having to log on"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000073','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1073','SV-53189r2_rule','default','high','Systems must be maintained at a supported service pack level.','"Systems at unsupported service packs or releases will not receive security updates for new vulnerabilities; which leave them subject to exploitation.  Systems must be maintained at a service pack level supported by the vendor with new security updates."','F-46115r1_fix','Update the system to a supported release or service pack level.','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-GE-000001','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26499','SV-53025r1_rule','default','medium','Unauthorized accounts must not have the Perform volume maintenance tasks user right.','"Inappropriate granting of user rights can provide system; administrative; and other high-level capabilities.Accounts with the ""Perform volume maintenance tasks"" user right can manage volume and disk configurations.  They could potentially delete volumes; resulting in data loss or a DoS."','F-45951r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> User Rights Assignment -> ""Perform volume maintenance tasks"" to only include the following accounts or groups:Administrators"','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-UR-000035','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-14259','SV-52997r1_rule','default','medium','Printing over HTTP must be prevented.','"Some features may communicate with the vendor; sending system information or downloading data or components for the feature.  Turning off this capability will prevent potentially sensitive information from being sent outside the enterprise and uncontrolled updates to the system.  This setting prevents the client computer from printing over HTTP; which allows the computer to print to printers on the intranet as well as the Internet."','F-45924r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> System -> Internet Communication Management -> Internet Communication settings -> ""Turn off printing over HTTP"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000039','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26498','SV-53029r1_rule','default','medium','Unauthorized accounts must not have the Modify firmware environment values user right.','"Inappropriate granting of user rights can provide system; administrative; and other high-level capabilities.Accounts with the ""Modify firmware environment values"" user right can change hardware configuration environment variables.  This could result in hardware failures or a DoS."','F-45955r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> User Rights Assignment -> ""Modify firmware environment values"" to only include the following accounts or groups:Administrators"','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-UR-000034','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26576','SV-52969r1_rule','default','medium','The IP-HTTPS IPv6 transition technology must be disabled.','"IPv6 transition technologies; which tunnel packets through other protocols; do not provide visibility."','F-45895r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Network -> TCPIP Settings -> IPv6 Transition Technologies -> ""Set IP-HTTPS State"" to ""Enabled: Disabled State"".  Note: ""IPHTTPS URL:"" must be entered in the policy even if set to Disabled State.  Enter ""about:blank""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000008','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26577','SV-52968r1_rule','default','medium','The ISATAP IPv6 transition technology must be disabled.','"IPv6 transition technologies; which tunnel packets through other protocols; do not provide visibility."','F-45894r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Network -> TCPIP Settings -> IPv6 Transition Technologies -> ""Set ISATAP State"" to ""Enabled: Disabled State""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000009','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26575','SV-52970r1_rule','default','medium','The 6to4 IPv6 transition technology must be disabled.','"IPv6 transition technologies; which tunnel packets through other protocols; do not provide visibility."','F-45896r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Network -> TCPIP Settings -> IPv6 Transition Technologies -> ""Set 6to4 State"" to ""Enabled: Disabled State""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000007','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-34974','SV-52954r1_rule','default','high','The Windows Installer Always install with elevated privileges option must be disabled.','Standard user accounts must not be granted elevated privileges.  Enabling Windows Installer to elevate privileges when installing applications can allow malicious persons and applications to gain full control of a system.','F-45880r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Windows Installer -> ""Always install with elevated privileges"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000116','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1174','SV-52878r3_rule','default','low','The amount of idle time required before suspending a session must be properly set.','"Open sessions can increase the avenues of attack on a system.  This setting is used to control when a computer disconnects an inactive SMB session.  If client activity resumes; the session is automatically reestablished.  This protects critical and sensitive network data from exposure to unauthorized personnel with physical access to the computer."','F-45804r2_fix','"Configure the policy value for Computer Configuration >> Windows Settings >> Security Settings >> Local Policies >> Security Options >> ""Microsoft Network Server: Amount of idle time required before suspending session"" to ""15"" minutes or less."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000031','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-3383','SV-52896r1_rule','default','medium','"The system must be configured to use FIPS-compliant algorithms for encryption; hashing; and signing."','"This setting ensures that the system uses algorithms that are FIPS-compliant for encryption; hashing; and signing.  FIPS-compliant algorithms meet specific standards established by the U.S. Government and must be the algorithms used for all OS encryption functions."','F-45822r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""System cryptography: Use FIPS compliant algorithms for encryption; hashing; and signing"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000074','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1173','SV-52877r1_rule','default','low','The default permissions of global system objects must be increased.','"Windows systems maintain a global list of shared system resources such as DOS device names; mutexes; and semaphores.  Each type of object is created with a default DACL that specifies who can access the objects with what permissions.  If this policy is enabled; the default DACL is stronger; allowing nonadministrative users to read shared objects; but not modify shared objects that they did not create."','F-45803r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""System objects: Strengthen default permissions of internal system objects (e.g. Symbolic Links)"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000076','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-3381','SV-52894r1_rule','default','medium','The system must be configured to the required LDAP client signing level.','"This setting controls the signing requirements for LDAP clients.  This setting must be set to Negotiate signing or Require signing; depending on the environment and type of LDAP server in use."','F-45820r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Network security: LDAP client signing requirements"" to ""Negotiate signing"" at a minimum."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000068','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-3380','SV-52893r1_rule','default','medium','The system must be configured to force users to log off when their allowed logon hours expire.','"Limiting logon hours can help protect data by only allowing access during specified times.  This setting controls whether or not users are forced to log off when their allowed logon hours expire.  If logon hours are set for users; this must be enforced."','F-45819r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Network security: Force logoff when logon hours expire"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000066','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-14229','SV-52943r1_rule','default','medium','Auditing of Backup and Restore Privileges must be turned off.','"Maintaining an audit trail of system activity logs can help identify configuration errors; troubleshoot service disruptions; and analyze compromises that have occurred; as well as detect attacks.  Audit logs are necessary to provide a trail of evidence in case the system or network is compromised.  Collecting this data is essential for analyzing the security of information assets and detecting signs of suspicious and unexpected behavior.  This setting prevents the system from generating audit events for every file backed up or restored; which could fill the security log in Windows; making it difficult to identify actual issues."','F-45869r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Audit: Audit the use of Backup and Restore privilege"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000008','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26479','SV-52113r2_rule','default','high','Unauthorized accounts must not have the Create a token object user right.','"Inappropriate granting of user rights can provide system; administrative; and other high-level capabilities.The ""Create a token object"" user right allows a process to create an access token. This could be used to provide elevated rights and compromise a system."','F-45138r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> User Rights Assignment -> ""Create a token object"" to be defined but containing no entries (blank)."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-UR-000012','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26478','SV-53063r1_rule','default','medium','Unauthorized accounts must not have the Create a pagefile user right.','"Inappropriate granting of user rights can provide system; administrative; and other high-level capabilities.Accounts with the ""Create a pagefile"" user right can change the size of a pagefile; which could affect system performance."','F-45989r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> User Rights Assignment -> ""Create a pagefile"" to only include the following accounts or groups:Administrators"','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-UR-000011','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26477','SV-53117r1_rule','default','low','Unauthorized accounts must not have the Change the time zone user right.','"Inappropriate granting of user rights can provide system; administrative; and other high-level capabilities.Accounts with the ""Change the time zone"" user right can change the time zone of a system."','F-46043r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> User Rights Assignment -> ""Change the time zone"" to only include the following accounts or groups:AdministratorsLocal Service"','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-UR-000010','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26476','SV-53118r1_rule','default','medium','Unauthorized accounts must not have the Change the system time user right.','"Inappropriate granting of user rights can provide system; administrative; and other high-level capabilities.Accounts with the ""Change the system time"" user right can change the system time; which can impact authentication; as well as affect time stamps on event log entries."','F-46044r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> User Rights Assignment -> ""Change the system time"" to only include the following accounts or groups:AdministratorsLocal Service"','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-UR-000009','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26602','SV-52237r3_rule','default','medium','The Microsoft FTP service must not be installed.','Unnecessary services increase the attack surface of a system. Some of these services may not support required levels of authentication or encryption.','F-74887r1_fix','"Remove or disable the ""Microsoft FTP Service"" (Service name: FTPSVC).   To remove the ""FTP Server"" role from a system:Start ""Server Manager""Select the server with the ""FTP Server"" role.Scroll down to ""ROLES AND FEATURES"" in the left pane.Select ""Remove Roles and Features"" from the drop down ""TASKS"" list.Select the appropriate server on the ""Server Selection"" page; click ""Next"".De-select ""FTP Server"" under ""Web Server (IIS).Click ""Next"" and ""Remove"" as prompted."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SV-000101','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-3469','SV-52906r1_rule','default','medium','Group Policies must be refreshed in the background if the user is logged on.','"If this setting is enabled; then Group Policy settings are not refreshed while a user is currently logged on.  This could lead to instances when a user does not have the latest changes to a policy applied and is therefore operating in an insecure context."','F-45832r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> System -> Group Policy -> ""Turn off background refresh of Group Policy"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000029','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26473','SV-53119r2_rule','default','medium','The Allow log on through Remote Desktop Services user right must only be assigned to the Administrators group.','"Inappropriate granting of user rights can provide system; administrative; and other high-level capabilities.Accounts with the ""Allow log on through Remote Desktop Services"" user right can access a system through Remote Desktop."','F-74895r1_fix','"Configure the policy value for Computer Configuration >> Windows Settings >> Security Settings >> Local Policies >> User Rights Assignment >> ""Allow log on through Remote Desktop Services"" to only include the following accounts or groups:Administrators"','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-UR-000006-DC','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-36712','SV-51752r1_rule','default','high','The Windows Remote Management (WinRM) client must not use Basic authentication.','Basic authentication uses plain text passwords that could be used to compromise a system.','F-44827r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Windows Remote Management (WinRM) -> WinRM Client -> ""Allow Basic authentication"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000123','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26471','SV-52109r2_rule','default','medium','Unauthorized accounts must not have the Adjust memory quotas for a process user right.','"Inappropriate granting of user rights can provide system; administrative; and other high-level capabilities.Accounts with the ""Adjust memory quotas for a process"" user right can adjust memory that is available to processes; and could be used in a denial of service (DoS) attack."','F-45134r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> User Rights Assignment -> ""Adjust memory quotas for a process"" to only include the following accounts or groups:AdministratorsLocal ServiceNetwork Service"','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-UR-000004','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26470','SV-51142r2_rule','default','medium','Unauthorized accounts must not have the Access this computer from the network user right on domain controllers.','"Inappropriate granting of user rights can provide system; administrative; and other high-level capabilities.Accounts with the ""Access this computer from the network"" right may access resources on the system and should be limited to those requiring it."','F-44299r2_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> User Rights Assignment -> ""Access this computer from the network"" to only include the following accounts or groups:AdministratorsAuthenticated UsersEnterprise Domain Controllers"','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-UR-000002-DC','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-15700','SV-53094r1_rule','default','medium','Remote access to the Plug and Play interface must be disabled for device installation.','Remote access to the Plug and Play interface could potentially allow connections by unauthorized devices.  This setting configures remote access to the Plug and Play interface and must be disabled.','F-46020r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> System -> Device Installation -> ""Allow remote access to the Plug and Play interface"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000019','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-15823','SV-53141r2_rule','default','medium','Software certificate installation files must be removed from a system.','Use of software certificates and their accompanying installation files for end users to access resources is less secure than the use of hardware-based certificates.','F-46067r1_fix','"Remove any certificate installation files (*.p12 and *.pfx) found on a system.This does not apply to server-based applications that have a requirement for .p12 certificate files (e.g.; Oracle Wallet Manager)."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-GE-000020','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-15722','SV-53139r1_rule','default','medium','Windows Media Digital Rights Management (DRM) must be prevented from accessing the Internet.','"Some features may communicate with the vendor; sending system information or downloading data or components for the feature.  Turning off this capability will prevent potentially sensitive information from being sent outside the enterprise and uncontrolled updates to the system.  This check verifies that Windows Media DRM will be prevented from accessing the Internet."','F-46065r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Windows Media Digital Rights Management -> ""Prevent Windows Media DRM Internet Access"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000120','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-16000','SV-52230r2_rule','default','medium','The system must be configured to ensure smart card devices can be redirected to the Remote Desktop session.  (Remote Desktop Services Role).','Enabling the redirection of smart card devices allows their use within Remote Desktop sessions.','F-45247r2_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Remote Desktop Services -> Remote Desktop Session Host -> Device and Resource Redirection -> ""Do not allow smart card device redirection"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000134','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-56511','SV-71667r1_rule','default','medium','The Windows Error Reporting Service must be running and configured to start automatically.','"Windows Error Reporting information can be used to help diagnose day-to-day software issues; as well as help discover malicious code and possibly zero-day attacks on systems."','F-62433r2_fix','"Configure the Start Type of the Windows Error Reporting Service to ""Automatic"" and ensure the service has a status of ""Running""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-ER-000001','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-16005','SV-52232r2_rule','default','low','The system must be configured to remove the Disconnect option from the Shut Down dialog box on the Remote Desktop Client.  (Remote Desktop Services Role).','Removing the Disconnect option from the Shut Down dialog box for Remote Desktop sessions helps prevent disconnected but active sessions from continuing to run and using resources.','F-45248r2_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Remote Desktop Services -> Remote Desktop Session Host -> Remote Session Environment -> ""Remove ""Disconnect"" option from Shut Down dialog"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000137','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-16008','SV-53142r1_rule','default','medium','"Windows must elevate all applications in User Account Control; not just signed ones."','"User Account Control (UAC) is a security mechanism for limiting the elevation of privileges; including administrative accounts; unless authorized.  This setting configures whether Windows elevates all applications; or only signed ones."','F-46068r2_fix','"UAC requirements are NA on Server Core installations.Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""User Account Control: Only elevate executables that are signed and validated"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000081','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-15704','SV-53116r1_rule','default','low','Errors in handwriting recognition on tablet PCs must not be reported to Microsoft.','"Some features may communicate with the vendor; sending system information or downloading data or components for the feature.  Turning off this capability will prevent potentially sensitive information from being sent outside the enterprise and uncontrolled updates to the system.  This setting prevents errors in handwriting recognition on tablet PCs from being reported to Microsoft."','F-46042r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> System -> Internet Communication Management -> Internet Communication settings -> ""Turn off handwriting recognition error reporting"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000035','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26503','SV-52121r2_rule','default','medium','Unauthorized accounts must not have the Replace a process level token user right.','"Inappropriate granting of user rights can provide system; administrative; and other high-level capabilities.The ""Replace a process level token"" user right allows one process or service to start another process or service with a different security access token.  A user with this right could use this to impersonate another account."','F-45146r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> User Rights Assignment -> ""Replace a process level token"" to only include the following accounts or groups:Local ServiceNetwork Service"','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-UR-000039','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26501','SV-53019r1_rule','default','medium','Unauthorized accounts must not have the Profile system performance user right.','"Inappropriate granting of user rights can provide system; administrative; and other high-level capabilities.Accounts with the ""Profile system performance"" user right can monitor system processes performance.  An attacker could potentially use this to identify processes to attack."','F-45945r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> User Rights Assignment -> ""Profile system performance"" to only include the following accounts or groups:AdministratorsNT Service\WdiServiceHost"','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-UR-000037','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26500','SV-53022r1_rule','default','medium','Unauthorized accounts must not have the Profile single process user right.','"Inappropriate granting of user rights can provide system; administrative; and other high-level capabilities.Accounts with the ""Profile single process"" user right can monitor nonsystem processes performance.  An attacker could potentially use this to identify processes to attack."','F-45948r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> User Rights Assignment -> ""Profile single process"" to only include the following accounts or groups:Administrators"','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-UR-000036','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26483','SV-51145r1_rule','default','medium','The Deny log on as a batch job user right on domain controllers must be configured to prevent unauthenticated access.','"Inappropriate granting of user rights can provide system; administrative; and other high-level capabilities.The ""Deny log on as a batch job"" user right defines accounts that are prevented from logging on to the system as a batch job; such as Task Scheduler.  The Guests group must be assigned to prevent unauthenticated access."','F-44302r2_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> User Rights Assignment -> ""Deny log on as a batch job"" to include the following:Guests Group"','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-UR-000018-DC','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26506','SV-52123r2_rule','default','medium','Unauthorized accounts must not have the Take ownership of files or other objects user right.','"Inappropriate granting of user rights can provide system; administrative; and other high-level capabilities.Accounts with the ""Take ownership of files or other objects"" user right can take ownership of objects and make changes."','F-45148r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> User Rights Assignment -> ""Take ownership of files or other objects"" to only include the following accounts or groups:Administrators"','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-UR-000042','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26505','SV-53016r1_rule','default','medium','Unauthorized accounts must not have the Shut down the system user right.','"Inappropriate granting of user rights can provide system; administrative; and other high-level capabilities.Accounts with the ""Shut down the system"" user right can interactively shut down a system; which could result in a DoS."','F-45943r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> User Rights Assignment -> ""Shut down the system"" to only include the following accounts or groups:Administrators"','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-UR-000041','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26504','SV-52122r2_rule','default','medium','Unauthorized accounts must not have the Restore files and directories user right.','"Inappropriate granting of user rights can provide system; administrative; and other high-level capabilities.Accounts with the ""Restore files and directories"" user right can circumvent file and directory permissions and could allow access to sensitive data.  It could also be used to overwrite more current data."','F-45147r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> User Rights Assignment -> ""Restore files and directories"" to only include the following accounts or groups:Administrators"','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-UR-000040','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1165','SV-52873r1_rule','default','low','The computer account password must not be prevented from being reset.','Computer account passwords are changed automatically on a regular basis.  Disabling automatic password changes can make the system more vulnerable to malicious access.  Frequent password changes can be a significant safeguard for your system.  A new password for the computer account will be generated every 30 days.','F-45799r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Domain member: Disable machine account password changes"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000015','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1164','SV-52872r2_rule','default','medium','Outgoing secure channel traffic must be signed when possible.','"Requests sent on the secure channel are authenticated; and sensitive information (such as passwords) is encrypted; but the channel is not integrity checked.  If this policy is enabled; outgoing secure channel traffic will be signed."','F-45798r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Domain member: Digitally sign secure channel data (when possible)"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000014','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1166','SV-52874r2_rule','default','medium','The Windows SMB client must be enabled to perform SMB packet signing when possible.','"The server message block (SMB) protocol provides the basis for many network operations.   If this policy is enabled; the SMB client will request packet signing when communicating with an SMB server that is enabled or required to perform SMB packet signing."','F-45800r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Microsoft network client: Digitally sign communications (if server agrees)"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000029','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-3378','SV-52891r1_rule','default','medium','The system must be configured to use the Classic security model.','"Windows includes two network-sharing security models - Classic and Guest only.  With the Classic model; local accounts must be password protected; otherwise; anyone can use guest user accounts to access shared system resources."','F-45817r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Network access: Sharing and security model for local accounts"" to ""Classic - local users authenticate as themselves""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000060','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1163','SV-52871r2_rule','default','medium','Outgoing secure channel traffic must be encrypted when possible.','"Requests sent on the secure channel are authenticated; and sensitive information (such as passwords) is encrypted; but not all information is encrypted.  If this policy is enabled; outgoing secure channel traffic will be encrypted."','F-45797r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Domain member: Digitally encrypt secure channel data (when possible)"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000013','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1162','SV-52870r2_rule','default','medium','The Windows SMB server must perform SMB packet signing when possible.','"The server message block (SMB) protocol provides the basis for many network operations.   Digitally signed SMB packets aid in preventing man-in-the-middle attacks.  If this policy is enabled; the SMB server will negotiate SMB packet signing as requested by the client."','F-45796r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Microsoft network server: Digitally sign communications (if client agrees)"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000033','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-3470','SV-52917r1_rule','default','medium','The system must be configured to prevent unsolicited remote assistance offers.','Remote assistance allows another user to view or take control of the local session of a user.  Unsolicited remote assistance is help that is offered by the remote user.  This may allow unauthorized parties access to the resources on the computer.','F-45843r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> System -> Remote Assistance -> ""Configure Offer Remote Assistance"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000058','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-3385','SV-52897r1_rule','default','medium','The system must be configured to require case insensitivity for non-Windows subsystems.','"This setting controls the behavior of non-Windows subsystems when dealing with the case of arguments or commands.  Case sensitivity could lead to the access of files or commands that must be restricted.  To prevent this from happening; case insensitivity restrictions must be required."','F-45823r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""System objects: Require case insensitivity for non-Windows subsystems"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000075','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1089','SV-52845r2_rule','default','medium','The required legal notice must be configured to display before console logon.','Failure to display the logon banner prior to a logon attempt will negate legal proceedings resulting from unauthorized access to system resources.','F-45771r2_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Interactive Logon: Message text for users attempting to log on"" to the following:You are accessing a U.S. Government (USG) Information System (IS) that is provided for USG-authorized use only.By using this IS (which includes any device attached to this IS); you consent to the following conditions:-The USG routinely intercepts and monitors communications on this IS for purposes including; but not limited to; penetration testing; COMSEC monitoring; network operations and defense; personnel misconduct (PM); law enforcement (LE); and counterintelligence (CI) investigations.-At any time; the USG may inspect and seize data stored on this IS.-Communications using; or data stored on; this IS are not private; are subject to routine monitoring; interception; and search; and may be disclosed or used for any USG-authorized purpose.-This IS includes security measures (e.g.; authentication and access controls) to protect USG interests--not for your personal benefit or privacy.-Notwithstanding the above; using this IS does not constitute consent to PM; LE or CI investigative searching or monitoring of the content of privileged communications; or work product; related to personal representation or services by attorneys; psychotherapists; or clergy; and their assistants.  Such communications and work product are private and confidential.  See User Agreement for details."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000022','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-36708','SV-51748r2_rule','default','medium','The location feature must be turned off.','The location service on systems may allow sensitive data to be used by applications on the system.  This should be turned off unless explicitly allowed for approved systems/applications.','F-44823r2_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Location and Sensors -> ""Turn off location"" to ""Enabled"".If location services are approved by the organization for a device; this must be documented."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000095','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26469','SV-53120r1_rule','default','medium','Unauthorized accounts must not have the Access Credential Manager as a trusted caller user right.','"Inappropriate granting of user rights can provide system; administrative; and other high-level capabilities.Accounts with the ""Access Credential Manager as a trusted caller"" user right may be able to retrieve the credentials of other accounts from Credential Manager."','F-46046r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> User Rights Assignment -> ""Access Credential Manager as a trusted caller"" to be defined but containing no entries (blank)."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-UR-000001','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-3479','SV-52920r1_rule','default','medium','The system must be configured to use Safe DLL Search Mode.','"The default search behavior; when an application calls a function in a Dynamic Link Library (DLL); is to search the current directory; followed by the directories contained in the systems path environment variable.  An unauthorized DLL; inserted into an applications working directory; could allow malicious code to be run on the system.  Setting this policy value forces the system to search the %Systemroot% for the DLL before searching the current directory or the rest of the path."','F-45846r2_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""MSS: (SafeDllSearchMode) Enable Safe DLL search mode (recommended)"" to ""Enabled"".(See ""Updating the Windows Security Options File"" in the STIG Overview document if MSS settings are not visible in the systems policy tools.)"','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000045','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1081','SV-52843r2_rule','default','high','Local volumes must be formatted using NTFS.','"The ability to set access permissions and auditing is critical to maintaining the security and proper access controls of a system.  To support this; volumes must be formatted using the NTFS file system."','F-45769r1_fix','Format all partitions/drives to use NTFS.','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-GE-000005','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-36700','SV-51740r1_rule','default','medium','The password reveal button must not be displayed.','"Visible passwords may be seen by nearby persons; compromising them.  The password reveal button can be used to display an entered password and must not be allowed."','F-44815r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Credential User Interface -> ""Do not display the password reveal button"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000076','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-14243','SV-52955r1_rule','default','medium','The system must require username and password to elevate a running application.','Enumeration of administrator accounts when elevating can provide part of the logon information to an unauthorized user.  This setting configures the system to always require users to type in a username and password to elevate a running application.','F-45881r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Credential User Interface -> ""Enumerate administrator accounts on elevation"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000077','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-8316','SV-51175r2_rule','default','high','Active Directory data files must have proper access control permissions.','"Improper access permissions for directory data related files could allow unauthorized users to read; modify; or delete directory data or audit trails."','F-44332r1_fix','"Ensure the permissions on NTDS database and log files are maintained as follows.NT AUTHORITY\SYSTEM:(I)(F)BUILTIN\Administrators:(I)(F)(I) - permission inherited from parent container(F) - full access"','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-AD-000001-DC','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26547','SV-52982r1_rule','default','medium','The system must be configured to audit Policy Change - Audit Policy Change failures.','"Maintaining an audit trail of system activity logs can help identify configuration errors; troubleshoot service disruptions; and analyze compromises that have occurred; as well as detect attacks.  Audit logs are necessary to provide a trail of evidence in case the system or network is compromised.  Collecting this data is essential for analyzing the security of information assets and detecting signs of suspicious and unexpected behavior.Audit Policy Change records events related to changes in audit policy."','F-45908r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Advanced Audit Policy Configuration -> System Audit Policies -> Policy Change -> ""Audit Audit Policy Change"" with ""Failure"" selected."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-AU-000086','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-14242','SV-52953r1_rule','default','medium','User Account Control must virtualize file and registry write failures to per-user locations.','"User Account Control (UAC) is a security mechanism for limiting the elevation of privileges; including administrative accounts; unless authorized.  This setting configures non-UAC-compliant applications to run in virtualized file and registry entries in per-user locations; allowing them to run."','F-45879r2_fix','"UAC requirements are NA on Server Core installations.Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""User Account Control: Virtualize file and registry write failures to per-user locations"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000085','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26578','SV-52967r1_rule','default','medium','The Teredo IPv6 transition technology must be disabled.','"IPv6 transition technologies; which tunnel packets through other protocols; do not provide visibility."','F-45893r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Network -> TCPIP Settings -> IPv6 Transition Technologies -> ""Set Teredo State"" to ""Enabled: Disabled State""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000010','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26582','SV-52963r2_rule','default','medium','The System event log size must be configured to 32768 KB or greater.','Inadequate log size will cause the log to fill up quickly. This may prevent audit events from being recorded properly and require frequent attention by administrative personnel.','F-71607r2_fix','"Configure the policy value for Computer Configuration >> Administrative Templates >> Windows Components >> Event Log Service >> System >> ""Specify the maximum log file size (KB)"" to ""Enabled"" with a ""Maximum Log Size (KB)"" of ""32768"" or greater."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000087','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26581','SV-52964r2_rule','default','medium','The Setup event log size must be configured to 32768 KB or greater.','Inadequate log size will cause the log to fill up quickly. This may prevent audit events from being recorded properly and require frequent attention by administrative personnel.','F-71605r2_fix','"Configure the policy value for Computer Configuration >> Administrative Templates >> Windows Components >> Event Log Service >> Setup >> ""Specify the maximum log file size (KB)"" to ""Enabled"" with a ""Maximum Log Size (KB)"" of ""32768"" or greater."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000086','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26580','SV-52965r2_rule','default','medium','The Security event log size must be configured to 196608 KB or greater.','Inadequate log size will cause the log to fill up quickly. This may prevent audit events from being recorded properly and require frequent attention by administrative personnel.','F-71603r2_fix','"Configure the policy value for Computer Configuration >> Administrative Templates >> Windows Components >> Event Log Service >> Security >> ""Specify the maximum log file size (KB)"" to ""Enabled"" with a ""Maximum Log Size (KB)"" of ""196608"" or greater."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000085','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-30016','SV-51143r2_rule','default','medium','Unauthorized accounts must not have the Add workstations to domain user right.','"Inappropriate granting of user rights can provide system; administrative; and other high-level capabilities.Accounts with the ""Add workstations to domain"" right may add computers to a domain.  This could result in unapproved or incorrectly configured systems being added to a domain."','F-44300r2_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> User Rights Assignment -> ""Add workstations to domain"" to only include the following accounts or groups:Administrators"','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-UR-000044-DC','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-15697','SV-53081r1_rule','default','medium','The Responder network protocol driver must be disabled.','The Responder network protocol driver allows a computer to be discovered and located on a network.  Disabling this helps protect the system from potentially being discovered and connected to by unauthorized devices.','F-46007r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Network -> Link-Layer Topology Discovery -> ""Turn on Responder (RSPNDR) driver"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000002','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-36707','SV-51747r2_rule','default','low','The Windows SmartScreen must be turned off.','Some features may send system information to the vendor.  Turning off this feature will prevent potentially sensitive information from being sent outside the enterprise.','F-44822r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> File Explorer -> ""Configure Windows SmartScreen"" to ""Enabled"" with ""Turn off SmartScreen"" selected."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000088','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-3449','SV-52216r2_rule','default','medium','Remote Desktop Services must limit users to one remote session.','Allowing multiple Remote Desktop Services sessions could consume resources.  There is also potential to make a secondary connection to a system with compromised credentials.','F-45235r2_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Remote Desktop Services -> Remote Desktop Session Host -> Connections -> ""Restrict Remote Desktop Services users to a single Remote Desktop Services Session"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000131','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-6836','SV-52938r2_rule','default','medium','"Passwords must; at a minimum; be 14 characters."','"Information systems not protected with strong password schemes (including passwords of minimum length) provide the opportunity for anyone to crack the password; thus gaining access to the system and compromising the device; information; or the local network."','F-45864r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Account Policies -> Password Policy -> ""Minimum password length"" to ""14"" characters."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-AC-000007','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26543','SV-52987r1_rule','default','medium','The system must be configured to audit Logon/Logoff - Special Logon successes.','"Maintaining an audit trail of system activity logs can help identify configuration errors; troubleshoot service disruptions; and analyze compromises that have occurred; as well as detect attacks.  Audit logs are necessary to provide a trail of evidence in case the system or network is compromised.  Collecting this data is essential for analyzing the security of information assets and detecting signs of suspicious and unexpected behavior.Special Logon records special logons which have administrative privileges and can be used to elevate processes."','F-45913r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Advanced Audit Policy Configuration -> System Audit Policies -> Logon/Logoff -> ""Audit Special Logon"" with ""Success"" selected."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-AU-000053','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-6834','SV-52937r1_rule','default','high','Anonymous access to Named Pipes and Shares must be restricted.','"Allowing anonymous access to named pipes or shares provides the potential for unauthorized system access.  This setting restricts access to those defined in ""Network access: Named Pipes that can be accessed anonymously"" and ""Network access: Shares that can be accessed anonymously"";  both of which must be blank under other requirements."','F-45863r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Network access: Restrict anonymous access to Named Pipes and Shares"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000058','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-6832','SV-52935r2_rule','default','medium','The Windows SMB client must be configured to always perform SMB packet signing.','"The server message block (SMB) protocol provides the basis for many network operations.  Digitally signed SMB packets aid in preventing man-in-the-middle attacks.  If this policy is enabled; the SMB client will only communicate with an SMB server that performs SMB packet signing."','F-45861r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Microsoft network client: Digitally sign communications (always)"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000028','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-6833','SV-52936r2_rule','default','medium','The Windows SMB server must be configured to always perform SMB packet signing.','"The server message block (SMB) protocol provides the basis for many network operations.  Digitally signed SMB packets aid in preventing man-in-the-middle attacks.  If this policy is enabled; the SMB server will only communicate with an SMB client that performs SMB packet signing."','F-45862r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Microsoft network server: Digitally sign communications (always)"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000032','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-11806','SV-52941r1_rule','default','low','The system must be configured to prevent the display of the last username on the logon screen.','Displaying the username of the last logged on user provides half of the userid/password equation that an unauthorized person would need to gain access.  The username of the last user to log on to a system must not be displayed.','F-45867r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Interactive logon: Do not display last user name"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000018','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-6831','SV-52934r2_rule','default','medium','Outgoing secure channel traffic must be encrypted or signed.','"Requests sent on the secure channel are authenticated; and sensitive information (such as passwords) is encrypted; but not all information is encrypted.  If this policy is enabled; outgoing secure channel traffic will be encrypted and signed."','F-45860r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Domain member: Digitally encrypt or sign secure channel data (always)"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000012','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1158','SV-52868r2_rule','default','low','The Recovery Console SET command must be disabled.','"The Recovery Console SET command allows environment variables to be set in the Recovery Console.  This permits access to all drives and folders  and the copying of files to removable media; which could expose sensitive information."','F-45794r2_fix','"Configure the policy value for Computer Configuration >> Windows Settings >> Security Settings >> Local Policies >> Security Options >> ""Recovery Console: Allow floppy copy and access to all drives and all folders"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000072','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1159','SV-52869r2_rule','default','high','The Recovery Console option must be set to prevent automatic logon to the system.','"If this option is enabled; the Recovery Console does not require a password and automatically logs on to the system.  This could allow unauthorized administrative access to the system."','F-45795r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Recovery console: Allow automatic administrative logon"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000071','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26541','SV-52994r2_rule','default','medium','The system must be configured to audit Logon/Logoff - Logon successes.','"Maintaining an audit trail of system activity logs can help identify configuration errors; troubleshoot service disruptions; and analyze compromises that have occurred; as well as detect attacks.  Audit logs are necessary to provide a trail of evidence in case the system or network is compromised.  Collecting this data is essential for analyzing the security of information assets and detecting signs of suspicious and unexpected behavior.Logon records user logons.  If this is an interactive logon; it is recorded on the local system.  If it is to a network share; it is recorded on the system accessed."','F-45921r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Advanced Audit Policy Configuration -> System Audit Policies -> Logon/Logoff -> ""Audit Logon"" with ""Success"" selected."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-AU-000047','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-14261','SV-53000r1_rule','default','medium','Windows must be prevented from using Windows Update to search for drivers.','"Some features may communicate with the vendor; sending system information or downloading data or components for the feature.  Turning off this capability will prevent potentially sensitive information from being sent outside the enterprise and uncontrolled updates to the system.  This setting prevents Windows from searching Windows Update for device drivers when no local drivers for a device are present."','F-45927r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> System -> Internet Communication Management -> Internet Communication settings -> ""Turn off Windows Update device driver searching"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000047','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-14260','SV-52998r1_rule','default','medium','Downloading print driver packages over HTTP must be prevented.','"Some features may communicate with the vendor; sending system information or downloading data or components for the feature.  Turning off this capability will prevent potentially sensitive information from being sent outside the enterprise and uncontrolled updates to the system.  This setting prevents the computer from downloading print driver packages over HTTP."','F-45925r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> System -> Internet Communication Management -> Internet Communication settings -> ""Turn off downloading of print drivers over HTTP"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000032','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1150','SV-52863r2_rule','default','medium','The built-in Windows password complexity policy must be enabled.','"The use of complex passwords increases their strength against attack. The built-in Windows password complexity policy requires passwords to contain at least 3 of the 4 types of characters (numbers; upper- and lower-case letters; and special characters); as well as preventing the inclusion of user names or parts of."','F-45789r2_fix','"Configure the policy value for Computer Configuration >> Windows Settings -> Security Settings >> Account Policies >> Password Policy >> ""Password must meet complexity requirements"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-AC-000008','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1151','SV-52214r2_rule','default','low','The print driver installation privilege must be restricted to administrators.','Allowing users to install drivers can introduce malware or cause the instability of a system.  Print driver installation should be restricted to administrators.','F-45233r2_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Devices: Prevent users from installing printer drivers"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000089','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1153','SV-52865r1_rule','default','high','"The LanMan authentication level must be set to send NTLMv2 response only; and to refuse LM and NTLM."','"The Kerberos v5 authentication protocol is the default for authentication of users who are logging on to domain accounts.  NTLM; which is less secure; is retained in later Windows versions  for compatibility with clients and servers that are running earlier versions of Windows or applications that still use it.  It is also used to authenticate logons to stand-alone computers that are running later versions."','F-45791r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Network security: LAN Manager authentication level"" to ""Send NTLMv2 response only. Refuse LM AND NTLM""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000067','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1154','SV-52866r1_rule','default','medium','The Ctrl+Alt+Del security attention sequence for logons must be enabled.','"Disabling the Ctrl+Alt+Del security attention sequence can compromise system security.  Because only Windows responds to the Ctrl+Alt+Del security sequence; a user can be assured that any passwords entered following that sequence are sent only to Windows.  If the sequence requirement is eliminated; malicious programs can request and receive a users Windows password.  Disabling this sequence also suppresses a custom logon banner."','F-45792r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Interactive Logon: Do not require CTRL+ALT+DEL"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000019','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1155','SV-51144r1_rule','default','medium','The Deny access to this computer from the network user right on domain controllers must be configured to prevent unauthenticated access.','"Inappropriate granting of user rights can provide system; administrative; and other high-level capabilities.The ""Deny access to this computer from the network"" user right defines the accounts that are prevented from logging on from the network.  The Guests group must be assigned this right to prevent unauthenticated access."','F-44301r2_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> User Rights Assignment -> ""Deny access to this computer from the network"" to include the following:Guests Group"','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-UR-000017-DC','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1157','SV-52867r2_rule','default','medium','The Smart Card removal option must be configured to Force Logoff or Lock Workstation.','Unattended systems are susceptible to unauthorized use and must be locked.  Configuring a system to lock when a smart card is removed will ensure the system is inaccessible when unattended.','F-45793r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Interactive logon: Smart card removal behavior"" to  ""Lock Workstation"" or ""Force Logoff""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000027','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1099','SV-52850r1_rule','default','medium','The lockout duration must be configured to require an administrator to unlock an account.','"The account lockout feature; when enabled; prevents brute-force password attacks on the system.  This parameter specifies the period of time that an account will remain locked after the specified number of failed logon attempts.  A value of 0 will require an administrator to unlock the account."','F-45776r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Account Policies -> Account Lockout Policy -> ""Account lockout duration"" to ""0"" minutes; ""Account is locked out until administrator unlocks it""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-AC-000001','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1098','SV-52849r1_rule','default','medium','The period of time before the bad logon counter is reset must meet minimum requirements.','"The account lockout feature; when enabled; prevents brute-force password attacks on the system.  This parameter specifies the period of time that must pass after failed logon attempts before the counter is reset to 0.  The smaller this value is; the less effective the account lockout feature will be in protecting the local system."','F-45775r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Account Policies -> Account Lockout Policy -> ""Reset account lockout counter after"" to at least ""60"" minutes."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-AC-000003','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-2372','SV-52880r1_rule','default','high','Reversible password encryption must be disabled.','"Storing passwords using reversible encryption is essentially the same as storing clear-text versions of the passwords.  For this reason; this policy must never be enabled."','F-45806r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Account Policies -> Password Policy -> ""Store password using reversible encryption"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-AC-000009','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-2374','SV-52879r2_rule','default','high','Autoplay must be disabled for all drives.','"Allowing Autoplay to execute may introduce malicious code to a system.  Autoplay begins reading from a drive as soon media is inserted into the drive.  As a result; the setup file of programs or music on audio media may start.  By default; Autoplay is disabled on removable drives; such as the floppy disk drive (but not the CD-ROM drive) and on network drives.  Enabling this policy disables Autoplay on all drives."','F-45805r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> AutoPlay Policies -> ""Turn off AutoPlay"" to ""Enabled:All Drives""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000074','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-2377','SV-51162r2_rule','default','medium','The Kerberos service ticket maximum lifetime must be limited to 600 minutes or less.','This setting determines the maximum amount of time (in minutes) that a granted session ticket can be used to access a particular service.  Session tickets are used only to authenticate new connections with servers.  Ongoing operations are not interrupted if the session ticket used to authenticate the connection expires during the connection.','F-44319r1_fix','"Configure the policy value in the Default Domain Policy for Computer Configuration ->  Policies -> Windows Settings -> Security Settings -> Account Policies -> Kerberos Policy -> ""Maximum lifetime for service ticket"" to a maximum of 600 minutes; but not 0 which equates to ""Ticket doesnt expire""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-AC-000011-DC','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-2376','SV-51160r2_rule','default','medium','Kerberos user logon restrictions must be enforced.','This policy setting determines whether the Kerberos Key Distribution Center (KDC) validates every request for a session ticket against the user rights policy of the target computer.  The policy is enabled by default which is the most secure setting for validating access to target resources is not circumvented.','F-44317r1_fix','"Configure the policy value in the Default Domain Policy for Computer Configuration ->  Policies -> Windows Settings -> Security Settings -> Account Policies -> Kerberos Policy -> ""Enforce user logon restrictions"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-AC-000010-DC','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-2379','SV-51166r2_rule','default','medium','The Kerberos policy user ticket renewal maximum lifetime must be limited to 7 days or less.','This setting determines the period of time (in days) during which a users TGT may be renewed.  This security configuration limits the amount of time an attacker has to crack the TGT and gain access.','F-44324r1_fix','"Configure the policy value in the Default Domain Policy for Computer Configuration ->  Policies -> Windows Settings -> Security Settings -> Account Policies -> Kerberos Policy -> ""Maximum lifetime for user ticket renewal"" to a maximum of 7 days or less."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-AC-000013-DC','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1090','SV-52846r2_rule','default','low','Caching of logon credentials must be limited.','"The default Windows configuration caches the last logon credentials for users who log on interactively to a system.  This feature is provided for system availability reasons; such as the users machine being disconnected from the network or domain controllers being unavailable.  Even though the credential cache is well-protected; if a system is attacked; an unauthorized individual may isolate the password to a domain user account using a password-cracking program and gain access to the domain."','F-66507r2_fix','"If the system is not a member of a domain; this is NA.Configure the policy value for Computer Configuration >> Windows Settings >> Security Settings >> Local Policies >> Security Options >> ""Interactive Logon: Number of previous logons to cache (in case Domain Controller is not available)"" to ""4"" logons or less."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000024','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1093','SV-52847r1_rule','default','high','Anonymous enumeration of shares must be restricted.','Allowing anonymous logon users (null session connections) to list all account names and enumerate all shared resources can provide a map of potential points to attack the system.','F-45773r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Network access: Do not allow anonymous enumeration of SAM accounts and shares"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000052','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1097','SV-52848r1_rule','default','medium','The number of allowed bad logon attempts must meet minimum requirements.','"The account lockout feature; when enabled; prevents brute-force password attacks on the system.  The higher this value is; the less effective the account lockout feature will be in protecting the local system.  The number of bad logon attempts must be reasonably small to minimize the possibility of a successful password attack; while allowing for honest errors made during a normal user logon."','F-45774r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Account Policies -> Account Lockout Policy -> ""Account lockout threshold"" to ""3"" or less invalid logon attempts (excluding ""0"" which is unacceptable)."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-AC-000002','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-3382','SV-52895r1_rule','default','medium','The system must be configured to meet the minimum session security requirement for NTLM SSP-based clients.','Microsoft has implemented a variety of security support providers for use with RPC sessions.  All of the options must be enabled to ensure the maximum security level.','F-45821r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Network security: Minimum session security for NTLM SSP based (including secure RPC) clients"" to ""Require NTLMv2 session security"" and ""Require 128-bit encryption"" (all options selected)."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000069','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-3373','SV-52887r1_rule','default','low','The maximum age for machine account passwords must be set to requirements.','"Computer account passwords are changed automatically on a regular basis.  This setting controls the maximum password age that a machine account may have.  This setting must be set to no more than 30 days; ensuring the machine changes its password monthly."','F-45813r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Domain member: Maximum machine account password age"" to ""30"" or less (excluding ""0"" which is unacceptable)."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000016','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-15991','SV-52223r2_rule','default','medium','UIAccess applications must not be allowed to prompt for elevation without using the secure desktop.','"User Account Control (UAC) is a security mechanism for limiting the elevation of privileges; including administrative accounts; unless authorized.  This setting prevents User Interface Accessibility programs from disabling the secure desktop for elevation prompts."','F-45241r1_fix','"UAC requirements are NA on Server Core installations.Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""User Account Control: Allow UIAccess applications to prompt for elevation without using the secure desktop"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000086','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-3376','SV-52889r1_rule','default','medium','The system must be configured to prevent the storage of passwords and credentials.','"This setting controls the storage of passwords and credentials for network authentication on the local system.  Such credentials must not be stored on the local machine; as that may lead to account compromise."','F-45815r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Network access: Do not allow storage of passwords and credentials for network authentication"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000053','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-15997','SV-52224r2_rule','default','medium','Users must be prevented from mapping local COM ports and redirecting data from the Remote Desktop Session Host to local COM ports.  (Remote Desktop Services Role).','Preventing the redirection of Remote Desktop session data to a client computers COM ports helps reduce possible exposure of sensitive data.','F-45242r2_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Remote Desktop Services -> Remote Desktop Session Host -> Device and Resource Redirection -> ""Do not allow COM port redirection"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000132','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-3374','SV-52888r2_rule','default','medium','The system must be configured to require a strong session key.','A computer connecting to a domain controller will establish a secure channel.  Requiring strong session keys enforces 128-bit encryption between systems.','F-45814r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Domain member: Require strong (Windows 2000 or Later) session key"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000017','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26359','SV-53121r2_rule','default','low','The Windows dialog box title for the legal banner must be configured.','Failure to display the logon banner prior to a logon attempt will negate legal proceedings resulting from unauthorized access to system resources.','F-46047r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Interactive Logon: Message title for users attempting to log on"" to ""DoD Notice and Consent Banner""; ""US Department of Defense Warning Statement""; or a site-defined equivalent. If a site-defined title is used; it can in no case contravene or modify the language of the banner text required in V-1089."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000023','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-15998','SV-52226r2_rule','default','medium','Users must be prevented from mapping local LPT ports and redirecting data from the Remote Desktop Session Host to local LPT ports.  (Remote Desktop Services Role).','Preventing the redirection of Remote Desktop session data to a client computers LPT ports helps reduce possible exposure of sensitive data.','F-45244r2_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Remote Desktop Services -> Remote Desktop Session Host -> Device and Resource Redirection -> ""Do not allow LPT port redirection"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000133','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-15999','SV-52229r2_rule','default','medium','Users must be prevented from redirecting Plug and Play devices to the Remote Desktop Session Host.  (Remote Desktop Services Role).','Preventing the redirection of Plug and Play devices in Remote Desktop sessions helps reduce possible exposure of sensitive data.','F-45246r2_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Remote Desktop Services -> Remote Desktop Session Host -> Device and Resource Redirection -> ""Do not allow supported Plug and Play device redirection"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000135','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-15682','SV-53040r1_rule','default','medium','Attachments must be prevented from being downloaded from RSS feeds.','Attachments from RSS feeds may not be secure.  This setting will prevent attachments from being downloaded from RSS feeds.','F-45966r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> RSS Feeds -> ""Prevent downloading of enclosures"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000105','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-15683','SV-53045r1_rule','default','medium','File Explorer shell protocol must run in protected mode.','The shell protocol will  limit the set of folders applications can open when run in protected mode.  Restricting files an application can open to a limited set of folders increases the security of Windows.','F-45971r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> File Explorer -> ""Turn off shell protocol protected mode"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000091','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1145','SV-52107r2_rule','default','medium','Automatic logons must be disabled.','Allowing a system to automatically log on when the machine is booted could give access to any unauthorized individual who restarts the computer.  Automatic logon with administrator privileges would give full access to an unauthorized individual.','F-45132r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""MSS: (AutoAdminLogon) Enable Automatic Logon (not recommended)"" to ""Disabled"".Ensure no passwords are stored in the ""DefaultPassword"" registry value noted below:Registry Hive: HKEY_LOCAL_MACHINERegistry Path: \Software\Microsoft\Windows NT\CurrentVersion\Winlogon\Value Name: DefaultPassword(See ""Updating the Windows Security Options File"" in the STIG Overview document if MSS settings are not visible in the systems policy tools.)"','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000036','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-15686','SV-53065r1_rule','default','low','Nonadministrators must be prevented from applying vendor-signed updates.','Uncontrolled system updates can introduce issues to a system.  This setting will prevent users from applying vendor-signed updates (though they may be from a trusted source).','F-45991r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Windows Installer -> ""Prohibit non-administrators from applying vendor signed updates"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000118','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-15687','SV-53069r1_rule','default','low','Users must not be presented with Privacy and Installation options on first use of Windows Media Player.','"Some features may communicate with the vendor; sending system information or downloading data or components for the feature.  Turning off this capability will prevent potentially sensitive information from being sent outside the enterprise and uncontrolled updates to the system.  This setting prevents users from being presented with Privacy and Installation options on first use of Windows Media Player; which could enable some communication with the vendor."','F-45995r1_fix','"If Windows Media Player is installed; configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Windows Media Player -> ""Do Not Show First Use Dialog Boxes"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000121','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1141','SV-52861r2_rule','default','medium','Unencrypted passwords must not be sent to third-party SMB Servers.','"Some non-Microsoft SMB servers only support unencrypted (plain text) password authentication.  Sending plain text passwords across the network; when authenticating to an SMB server; reduces the overall security of the environment.  Check with the vendor of the SMB server to see if there is a way to support encrypted password authentication."','F-45787r2_fix','"Configure the policy value for Computer Configuration >> Windows Settings >> Security Settings >> Local Policies >> Security Options >> ""Microsoft Network Client: Send unencrypted password to third-party SMB servers"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000030','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-15685','SV-53061r1_rule','default','medium','Users must be prevented from changing installation options.','Installation options for applications are typically controlled by administrators.  This setting prevents users from changing installation options that may bypass security features.','F-45986r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Windows Installer -> ""Allow user control over installs"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000115','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-57633','SV-72043r1_rule','default','medium','The system must be configured to audit Policy Change - Authorization Policy Change successes.','"Maintaining an audit trail of system activity logs can help identify configuration errors; troubleshoot service disruptions; and analyze compromises that have occurred; as well as detect attacks. Audit logs are necessary to provide a trail of evidence in case the system or network is compromised. Collecting this data is essential for analyzing the security of information assets and detecting signs of suspicious and unexpected behavior.Authorization Policy Change records events related to changes in user rights; such as Create a token object."','F-62835r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Advanced Audit Policy Configuration -> System Audit Policies -> Policy Change -> ""Audit Authorization Policy Change"" with ""Success"" selected."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-AU-000089','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1171','SV-52875r1_rule','default','medium','Ejection of removable NTFS media must be restricted to Administrators.','"Removable hard drives; if they are not properly configured; can be formatted and ejected by users who are not members of the Administrators Group.  Formatting and ejecting removable NTFS media must only be done by administrators."','F-45801r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Devices: Allowed to format and eject removable media"" to ""Administrators""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000011','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-57473','SV-71941r1_rule','default','medium','The maximum number of error reports to queue on a system must be configured to 50 or greater.','"The error reporting queue is stored locally on the system and contains the error reports until they have been manually removed or automatically sent to the local collector or DOD-wide collector (if defined).  Once a report has been sent to a collector; it is moved to the report archive.  Old reports are deleted as new ones arrive once the maximum limit has been met."','F-62747r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Windows Error Reporting -> Advanced Error Reporting Settings -> ""Configure Report Queue"" to ""Enabled"" with ""Maximum number of reports to queue:"" set to ""50"" or greater."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-ER-000015','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-57471','SV-71931r1_rule','default','medium','The system must be configured to add all error reports to the queue.','Error reports are queued for sending to an error reporting site when the queueing behavior is set to Always Queue.  This will maintain the reports in the queue until a connection can be made to the collection server.','F-62737r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Windows Error Reporting -> Advanced Error Reporting Settings -> ""Configure Report Queue"" to ""Enabled"" with ""Queuing behavior:"" to ""Always queue""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-ER-000014','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-57477','SV-71961r1_rule','default','medium','The system must be configured to automatically consent to send all data requested by a local or DOD-wide error collection site.','Configuring error reporting to send all requested data ensures all relevant data associated with the error report is captured for later analysis.  This setting determines the default consent behavior of Windows Error Reporting.','F-62767r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Windows Error Reporting -> Consent -> ""Configure Default consent"" to ""Enabled"" with ""Send all data"" selected for ""Consent level""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-ER-000017','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-36720','SV-51757r1_rule','default','medium','The Windows Remote Management (WinRM) service must not store RunAs credentials.','Storage of administrative credentials could allow unauthorized access.  Disallowing the storage of RunAs credentials for Windows Remote Management will prevent them from being used with plug-ins.','F-44832r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Windows Remote Management (WinRM) -> WinRM Service -> ""Disallow WinRM from storing RunAs credentials"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000128','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-36684','SV-51611r1_rule','default','medium','Local users on domain-joined computers must not be enumerated.','The username is one part of logon credentials that could be used to gain access to a system.  Preventing the enumeration of users limits this information to authorized personnel.','F-44732r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> System -> Logon -> ""Enumerate local users on domain-joined computers"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000051','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-36687','SV-51612r1_rule','default','medium','App notifications on the lock screen must be turned off.','App notifications that are displayed on the lock screen could display sensitive information to unauthorized personnel.  Turning off this feature will limit access to the information to a logged on user.','F-44733r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> System -> Logon -> ""Turn off app notifications on the lock screen"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000052','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-36681','SV-51610r1_rule','default','medium','Copying of user input methods to the system account for sign-in must be prevented.','Allowing different input methods for sign-in could open different avenues of attack.  User input methods must be restricted to those enabled for the system account at sign-in.','F-44731r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> System -> Locale Services -> ""Disallow copying of user input methods to the system account for sign-in"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000048','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-36680','SV-51609r2_rule','default','medium','Access to the Windows Store must be turned off.','"Uncontrolled installation of applications can introduce various issues; including system instability; and allow access to sensitive information.  Installation of applications must be controlled by the enterprise.  Turning off access to the Windows Store will limit access to publicly available applications."','F-74883r1_fix','"If the \Windows\WinStore directory exists; configure the policy value for Computer Configuration >> Administrative Templates >> System >> Internet Communication Management >> Internet Communication settings >> ""Turn off access to the Store"" to ""Enabled"".   Alternately; uninstall the ""Desktop Experience"" feature from Windows 2012.  This is located under ""User Interfaces and Infrastructure"" in the ""Add Roles and Features Wizard"".  The \Windows\WinStore directory may need to be manually deleted after this."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000030','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-3458','SV-52903r2_rule','default','medium','Remote Desktop Services must be configured to disconnect an idle session after the specified time period.','This setting controls how long a session may be idle before it is automatically disconnected from the server.  Users must disconnect if they plan on being away from their terminals for extended periods of time.  Idle sessions must be disconnected after 15 minutes.','F-45829r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Remote Desktop Services -> Remote Desktop Session Host -> Session Time Limits -> ""Set time limit for active but idle Remote Desktop Services sessions"" to ""Enabled""; and the ""Idle session limit"" to 15 minutes or less; excluding ""0""; which equates to ""Never""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000101','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-36719','SV-51756r2_rule','default','medium','The Windows Remote Management (WinRM) service must not allow unencrypted traffic.','Unencrypted remote access to a system can allow sensitive information to be compromised.  Windows remote management connections must be encrypted to prevent this.','F-44831r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Windows Remote Management (WinRM) -> WinRM Service -> ""Allow unencrypted traffic"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000127','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-14239','SV-52950r1_rule','default','medium','User Account Control must only elevate UIAccess applications that are installed in secure locations.','"User Account Control (UAC) is a security mechanism for limiting the elevation of privileges; including administrative accounts; unless authorized.  This setting configures Windows to only allow applications installed in a secure location on the file system; such as the Program Files or the Windows\System32 folders; to run with elevated privileges."','F-45876r2_fix','"UAC requirements are NA on Server Core installations.Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""User Account Control: Only elevate UIAccess applications that are installed in secure locations"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000082','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-3453','SV-52898r1_rule','default','medium','Remote Desktop Services must always prompt a client for passwords upon connection.','This setting controls the ability of users to supply passwords automatically as part of their remote desktop connection.  Disabling this setting would allow anyone to use the stored credentials in a connection item to connect to the terminal server.','F-45824r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Remote Desktop Services -> Remote Desktop Session Host -> Security -> ""Always prompt for password upon connection"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000099','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-36718','SV-51755r2_rule','default','high','The Windows Remote Management (WinRM) service must not use Basic authentication.','Basic authentication uses plain text passwords that could be used to compromise a system.','F-44830r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Windows Remote Management (WinRM) -> WinRM Service -> ""Allow Basic authentication"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000126','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-3457','SV-52902r2_rule','default','medium','Remote Desktop Services must be configured to set a time limit for disconnected sessions.','This setting controls how long a session will remain open if it is unexpectedly terminated.  Such sessions use system resources and must be terminated as soon as possible.','F-45828r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Remote Desktop Services -> Remote Desktop Session Host -> Session Time Limits -> ""Set time limit for disconnected sessions"" to ""Enabled""; and ""End a disconnected session"" to ""1 minute""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000102','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-3456','SV-52901r1_rule','default','medium','Remote Desktop Services must delete temporary folders when a session is terminated.','Remote desktop session temporary folders must always be deleted after a session is over to prevent hard disk clutter and potential leakage of information.  This setting controls the deletion of the temporary folders when the session is terminated.','F-45827r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Remote Desktop Services -> Remote Desktop Session Host -> Temporary Folders -> ""Do not delete temp folder upon exit"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000103','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-3455','SV-52900r1_rule','default','medium','Remote Desktop Services must be configured to use session-specific temporary folders.','"If a communal temporary folder is used for remote desktop sessions; it might be possible for users to access other users temporary folders.  If this setting is enabled; only one temporary folder is used for all remote desktop sessions.  Per session temporary folders must be established."','F-45826r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Remote Desktop Services -> Remote Desktop Session Host -> Temporary Folders -> ""Do not use temporary folders per session"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000104','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-3454','SV-52899r2_rule','default','medium','Remote Desktop Services must be configured with the client connection encryption set to the required level.','"Remote connections must be encrypted to prevent interception of data or sensitive information. Selecting ""High Level"" will ensure encryption of Remote Desktop Services sessions in both directions."','F-45825r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Remote Desktop Services -> Remote Desktop Session Host -> Security -> ""Set client connection encryption level"" to ""Enabled"" and ""High Level""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000100','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26600','SV-52236r2_rule','default','medium','The Fax service must be disabled if installed.','Unnecessary services increase the attack surface of a system. Some of these services may not support required levels of authentication or encryption.','F-45251r1_fix','Remove or disable the Fax (fax) service.','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SV-000100','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-15718','SV-53137r1_rule','default','low','Turning off File Explorer heap termination on corruption must be disabled.','Legacy plug-in applications may continue to function when a File Explorer session has become corrupt.  Disabling this feature will prevent this.','F-46063r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> File Explorer -> ""Turn off heap termination on corruption"" to ""Disabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000090','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-4407','SV-51140r3_rule','default','medium','Domain controllers must require LDAP access signing.','"Unsigned network traffic is susceptible to man in the middle attacks where an intruder captures packets between the server and the client and modifies them before forwarding them to the client.  In the case of an LDAP server; this means that an attacker could cause a client to make decisions based on false records from the LDAP directory.  You can lower the risk of an attacker pulling this off in a corporate network by implementing strong physical security measures to protect the network infrastructure.  Furthermore; implementing Internet Protocol security (IPSec) authentication header mode (AH); which performs mutual authentication and packet integrity for Internet Protocol (IP) traffic; can make all types of man in the middle attacks extremely difficult."','F-44297r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Domain controller: LDAP server signing requirements"" to ""Require signing""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000090-DC','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26283','SV-53122r1_rule','default','high','Anonymous enumeration of SAM accounts must not be allowed.','"Anonymous enumeration of SAM accounts allows anonymous log on users (null session connections) to list all accounts names; thus providing a list of potential points to attack the system."','F-46048r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Network access: Do not allow anonymous enumeration of SAM accounts"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000051','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26529','SV-53013r2_rule','default','medium','The system must be configured to audit Account Logon - Credential Validation successes.','"Maintaining an audit trail of system activity logs can help identify configuration errors; troubleshoot service disruptions; and analyze compromises that have occurred; as well as detect attacks.  Audit logs are necessary to provide a trail of evidence in case the system or network is compromised.  Collecting this data is essential for analyzing the security of information assets and detecting signs of suspicious and unexpected behavior.Credential validation records events related to validation tests on credentials for a user account logon."','F-45940r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Advanced Audit Policy Configuration -> System Audit Policies -> Account Logon -> ""Audit Credential Validation"" with ""Success"" selected."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-AU-000001','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26475','SV-52112r2_rule','default','low','Unauthorized accounts must not have the Bypass traverse checking user right.','"Inappropriate granting of user rights can provide system; administrative; and other high-level capabilities.Accounts with the ""Bypass traverse checking"" user right can pass through folders when browsing even if they do not have the ""Traverse Folder"" access permission. They could potentially view sensitive file and folder names.  They would not have additional access to the files and folders unless it is granted through permissions."','F-45137r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> User Rights Assignment -> ""Bypass traverse checking"" to only include the following accounts or groups:AdministratorsAuthenticated UsersLocal ServiceNetwork ServiceWindow Manager\Window Manager Group"','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-UR-000008','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26474','SV-52111r2_rule','default','medium','Unauthorized accounts must not have the back up files and directories user right.','"Inappropriate granting of user rights can provide system; administrative; and other high-level capabilities.Accounts with the ""Back up files and directories"" user right can circumvent file and directory permissions and could allow access to sensitive data."','F-45136r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> User Rights Assignment -> ""Back up files and directories"" to only include the following accounts or groups:Administrators"','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-UR-000007','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1136','SV-52860r1_rule','default','low','Users must be forcibly disconnected when their logon hours expire.','"Users must not be permitted to remain logged on to the network after they have exceeded their permitted logon hours.  In many cases; this indicates that a user forgot to log off before leaving for the day.  However; it may also indicate that a user is attempting unauthorized access at a time when the system may be less closely monitored.  Forcibly disconnecting users when logon hours expire protects critical and sensitive network data from exposure to unauthorized personnel with physical access to the computer."','F-45786r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Microsoft network server: Disconnect clients when logon hours expire"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000034','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-57475','SV-71951r1_rule','default','medium','The system must be configured to attempt to forward queued error reports once a day.','Error reports stored in the queue should be forwarded to a local or DOD-wide collection site when the system can connect to the site.  This setting controls the frequency a system will use to try forwarding queued reports to the local or DOD-wide collector.','F-62757r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Windows Error Reporting -> Advanced Error Reporting Settings -> ""Configure Report Queue"" to ""Enabled"" with ""Number of days between solution check reminders:"" set to ""1""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-ER-000016','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26604','SV-52238r2_rule','default','medium','The Peer Networking Identity Manager service must be disabled if installed.','Unnecessary services increase the attack surface of a system. Some of these services may not support required levels of authentication or encryption.','F-45253r1_fix','Remove or disable the Peer Networking Identity Manager (p2pimsvc) service.','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SV-000103','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-15674','SV-53021r1_rule','default','medium','The Internet File Association service must be turned off.','"Some features may communicate with the vendor; sending system information or downloading data or components for the feature.  Turning off this capability will prevent potentially sensitive information from being sent outside the enterprise and uncontrolled updates to the system.  This setting prevents unhandled file associations from using the Microsoft Web service to find an application."','F-45947r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> System -> Internet Communication Management -> Internet Communication settings -> ""Turn off Internet File Association service"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000038','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-1172','SV-52876r1_rule','default','low','Users must be warned in advance of their passwords expiring.','"Creating strong passwords that can be remembered by users requires some thought.  By giving the user advance warning; the user has time to construct a sufficiently strong password.  This setting configures the system to display a warning to users telling them how many days are left before their password expires."','F-45802r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""Interactive Logon: Prompt user to change password before expiration"" to ""14"" days or more."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000025','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-15672','SV-53017r1_rule','default','low','Event Viewer Events.asp links must be turned off.','"Viewing events is a function of administrators; who must not access the internet with privileged accounts.  This setting will disable  Events.asp hyperlinks in Event Viewer to prevent links to the internet from within events."','F-45944r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> System -> Internet Communication Management -> Internet Communication settings -> ""Turn off Event Viewer ""Events.asp"" links"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000033','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26472','SV-52110r2_rule','default','medium','Unauthorized accounts must not have the Allow log on locally user right.','"Inappropriate granting of user rights can provide system; administrative; and other high-level capabilities.Accounts with the ""Allow log on locally"" user right can log on interactively to a system."','F-45135r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> User Rights Assignment -> ""Allow log on locally"" to only include the following accounts or groups:Administrators"','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-UR-000005','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-14241','SV-52952r1_rule','default','medium','User Account Control must switch to the secure desktop when prompting for elevation.','"User Account Control (UAC) is a security mechanism for limiting the elevation of privileges; including administrative accounts; unless authorized.  This setting ensures that the elevation prompt is only used in secure desktop mode."','F-45878r2_fix','"UAC requirements are NA on Server Core installations.Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""User Account Control: Switch to the secure desktop when prompting for elevation"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000084','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-14240','SV-52951r1_rule','default','medium','"User Account Control must run all administrators in Admin Approval Mode; enabling UAC."','"User Account Control (UAC) is a security mechanism for limiting the elevation of privileges; including administrative accounts; unless authorized.  This setting enables UAC."','F-45877r2_fix','"UAC requirements are NA on Server Core installations.Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Local Policies -> Security Options -> ""User Account Control: Run all administrators in Admin Approval Mode"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-SO-000083','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26538','SV-53001r2_rule','default','medium','The system must be configured to audit Account Management - User Account Management failures.','"Maintaining an audit trail of system activity logs can help identify configuration errors; troubleshoot service disruptions; and analyze compromises that have occurred; as well as detect attacks.  Audit logs are necessary to provide a trail of evidence in case the system or network is compromised.  Collecting this data is essential for analyzing the security of information assets and detecting signs of suspicious and unexpected behavior.User Account Management records events such as creating; changing; deleting; renaming; disabling; or enabling user accounts."','F-45928r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Advanced Audit Policy Configuration -> System Audit Policies -> Account Management -> ""Audit User Account Management"" with ""Failure"" selected."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-AU-000020','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-26539','SV-52999r1_rule','default','medium','The system must be configured to audit Detailed Tracking - Process Creation successes.','"Maintaining an audit trail of system activity logs can help identify configuration errors; troubleshoot service disruptions; and analyze compromises that have occurred; as well as detect attacks.  Audit logs are necessary to provide a trail of evidence in case the system or network is compromised.  Collecting this data is essential for analyzing the security of information assets and detecting signs of suspicious and unexpected behavior.Process Creation records events related to the creation of a process and the source."','F-45926r1_fix','"Configure the policy value for Computer Configuration -> Windows Settings -> Security Settings -> Advanced Audit Policy Configuration -> System Audit Policies -> Detailed Tracking -> ""Audit Process Creation"" with ""Success"" selected."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-AU-000023','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-57465','SV-71899r1_rule','default','medium','The system must be configured to store all data in the error report archive.','"The error reporting archive is stored locally on the system and is created after an error report has been sent to the local collector or DOD-wide collector (if defined).  Storing all data; including memory contents; adds data that is very helpful in analyzing the errors."','F-62707r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Windows Error Reporting -> Advanced Error Reporting Settings -> ""Configure Report Archive"" to ""Enabled"" with ""Store All"" selected for ""Archive behavior:""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-ER-000011','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-14247','SV-52958r1_rule','default','medium','Passwords must not be saved in the Remote Desktop Client.','Saving passwords in the Remote Desktop Client could allow an unauthorized user to establish a remote desktop session to another system.  The system must be configured to prevent users from saving passwords in the Remote Desktop Client.','F-45884r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Remote Desktop Services -> Remote Desktop Connection Client -> ""Do not allow passwords to be saved"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000096','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-57467','SV-71909r1_rule','default','medium','The maximum number of error reports to archive on a system must be configured to 100 or greater.','The retention of archived reports provides a history.  Older reports are automatically deleted as new reports are generated once the maximum limit has been met.  The archive is stored locally on the system and is created after the error report has been sent to the local collector or DOD-wide collector (if defined).','F-62717r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Windows Error Reporting -> Advanced Error Reporting Settings -> ""Configure Report Archive"" to ""Enabled"" with ""Maximum number of reports to store:"" set to ""100"" or greater."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-ER-000012','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-14249','SV-52959r1_rule','default','medium','Local drives must be prevented from sharing with Remote Desktop Session Hosts.  (Remote Desktop Services Role).','Preventing users from sharing the local drives on their client computers to Remote Session Hosts that they access helps reduce possible exposure of sensitive data.','F-45885r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Remote Desktop Services -> Remote Desktop Session Host -> Device and Resource Redirection -> ""Do not allow drive redirection"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-CC-000098','None','None','Imported data from UCF - DISA') from dual;
select add_rule_data ('default','V-57469','SV-71921r1_rule','default','medium','The system must be configured to queue error reports until a local or DOD-wide collector is available.','Queueing error reports provides the ability for a system to collect reports locally or until a collection server can be contacted.  Valuable system diagnostic and vulnerability information may be lost if the report queue is disabled.','F-62727r1_fix','"Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> Windows Error Reporting -> Advanced Error Reporting Settings -> ""Configure Report Queue"" to ""Enabled""."','Windows Server 2012 - 2012 R2 Domain Controller',' III - Administrative Classified ','WN12-ER-000013','None','None','Imported data from UCF - DISA') from dual;
