select add_rule_data ('default','V-21973','SV-25299r1_rule','default','medium','Turn off autoplay for non-volume devices.','This setting will disable autoplay for non-volume devices (such as Media Transfer Protocol (MTP) devices).','F-22962r1_fix','Configure the policy value for Computer Configuration -> Administrative Templates -> Windows Components -> AutoPlay Policies -> “Turn off Autoplay for non-volume devices” to “Enabled”.','Windows 7',' I - Mission Critical Public ','5.280','None','None','Imported data from UCF - DISA') from dual;
